import React, { useState } from "react";

const BookAd = () => {
  const [selectedNewspaper, setSelectedNewspaper] = useState("");

  const handleNewspaperChange = (e) => {
    setSelectedNewspaper(e.target.value);
  };

  return (
    <div style={styles.bookAdContainer}>
      <div style={styles.bookAdBox}>
        <h2>Book Your Ad</h2>

        {/* Select Newspaper Dropdown */}
        <select
          style={styles.newspaperSelect}
          onChange={handleNewspaperChange}
          value={selectedNewspaper}
        >
          <option value="">-- Select --</option>
          <option value="The Virtual Times">The Virtual Times - ₹10 per word</option>
          <option value="Daily News">Daily News - ₹8 per word</option>
        </select>

        {/* Buttons */}
        <div style={styles.bookAdButtons}>
          <button>Submit</button>
          <button>Cancel</button>
        </div>
      </div>
    </div>
  );
};

// ✅ Inline Styles Object
const styles = {
  bookAdContainer: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    backgroundColor: "#f4f4f4",
    margin: "0",
    padding: "0",
    boxSizing: "border-box",
  },
  bookAdBox: {
    width: "400px",
    padding: "30px",
    background: "white",
    borderRadius: "12px",
    boxShadow: "0 8px 16px rgba(0, 0, 0, 0.1)",
    display: "flex",
    flexDirection: "column",
    textAlign: "center",
  },
  newspaperSelect: {
    margin: "15px auto", // Center the dropdown
    padding: "12px",
    width: "60%", // ✅ Smaller dropdown width
    border: "1px solid #ccc",
    borderRadius: "8px",
    boxSizing: "border-box",
  },
  bookAdButtons: {
    marginTop: "20px",
    display: "flex",
    justifyContent: "space-between",
  },
  button: {
    backgroundColor: "#007bff",
    color: "white",
    border: "none",
    padding: "12px 20px",
    borderRadius: "8px",
    cursor: "pointer",
    transition: "background 0.3s ease",
  },
  buttonHover: {
    backgroundColor: "#0056b3",
  },
};

export default BookAd;
