import React, { useEffect, useState } from "react";
import axios from "axios";

const AdBooking = () => {
  const [newspapers, setNewspapers] = useState([]);
  const [selectedNewspaper, setSelectedNewspaper] = useState("");
  const [adSpaces, setAdSpaces] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/newspapers")
      .then((res) => setNewspapers(res.data))
      .catch((error) => console.error("Error fetching newspapers:", error));
  }, []);

  const handleNewspaperSelect = async (e) => {
    const newspaperId = e.target.value;
    setSelectedNewspaper(newspaperId);

    if (newspaperId) {
      try {
        const response = await axios.get(`http://localhost:5000/api/adspaces/${newspaperId}`);
        setAdSpaces(response.data);
      } catch (error) {
        console.error("Error fetching ad spaces:", error);
      }
    } else {
      setAdSpaces([]); // Clear ad spaces if no newspaper is selected
    }
  };

  return (
    <div>
      <h2>Book an Ad</h2>
      
      {/* Newspaper Selection */}
      <label>Select Newspaper:</label>
      <select onChange={handleNewspaperSelect} value={selectedNewspaper}>
        <option value="">-- Select --</option>
        {newspapers.map((paper) => (
          <option key={paper.id} value={paper.id}>
            {paper.name}
          </option>
        ))}
      </select>

      {/* Display Ad Spaces */}
      {selectedNewspaper && (
        <div>
          <h3>Available Ad Spaces:</h3>
          {adSpaces.length > 0 ? (
            <ul>
              {adSpaces.map((space) => (
                <li key={space.id}>
                  {space.space_name} - ${space.price} - {space.status}
                </li>
              ))}
            </ul>
          ) : (
            <p>No ad spaces available for this newspaper.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default AdBooking;
