import React, { useState } from "react";
import "../../styles/NewspaperLayout.css"; // Ensure CSS path is correct

const NewspaperLayout = () => {
  const [selectedAd, setSelectedAd] = useState(null);
  const [selectedPrice, setSelectedPrice] = useState(null);
  const [bookingMessage, setBookingMessage] = useState("");

  // Prices for different ad sizes
  const adPrices = {
    large: 5000,
    medium: 3000,
    small: 1500,
  };

  const handleAdClick = (size, index) => {
    setSelectedAd(index);
    setSelectedPrice(adPrices[size]);
    setBookingMessage(""); // Reset message when selecting a new ad
  };

  const handleBookNow = async (index) => {
    const userId = localStorage.getItem("userId"); // Assuming user ID is stored in localStorage
    if (!userId) {
      alert("You must be logged in to book an ad!");
      return;
    }

    const bookingData = {
      adId: index,
      userId: userId,
      price: selectedPrice,
      status: "pending", // Default status before admin approval
    };

    try {
      const response = await fetch("http://localhost:5000/api/bookAd", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(bookingData),
      });

      const data = await response.json();
      if (response.ok) {
        setBookingMessage("Booking successful! Pending admin approval.");
      } else {
        setBookingMessage(`Error: ${data.message}`);
      }
    } catch (error) {
      setBookingMessage("Failed to book ad. Please try again.");
    }
  };

  return (
    <div className="newspaper-container">
      <div className="newspaper-content">
        <h1 className="newspaper-title">THE VIRTUAL TIMES</h1>
        <p className="sub-title">Your Trusted Daily Source</p>
        <div className="separator"></div> {/* Separation Line */}

        {/* Ad Sections */}
        {[
          { size: "large", id: 0, label: "Exclusive Front Page Ad Space" },
          { size: "medium", id: 1, label: "Premium Half-Page Ad" },
          { size: "small", id: 2, label: "Quarter-Page Ad" },
          { size: "small", id: 3, label: "Compact Ad Spot" },
          { size: "medium", id: 4, label: "Half-Page Visibility" },
          { size: "large", id: 5, label: "Prime Full-Width Ad Spot" },
          { size: "small", id: 6, label: "Limited-Time Ad Offer" },
          { size: "medium", id: 7, label: "Top Visibility Ad" },
          { size: "large", id: 8, label: "Ultimate Full-Page Ad Placement" },
        ].map((ad) => (
          <div key={ad.id} className="ads-container">
            <div className={`ad-space ${ad.size}`} onClick={() => handleAdClick(ad.size, ad.id)}>
              ðŸ”¹ **{ad.label}** ðŸ”¹
              {selectedAd === ad.id && (
                <div className="ad-details">
                  <p className="ad-price">Price: â‚¹{selectedPrice}</p>
                  <button className="book-now-btn" onClick={() => handleBookNow(ad.id)}>Book Now</button>
                </div>
              )}
            </div>
          </div>
        ))}

        {/* Booking Message Display */}
        {bookingMessage && <p className="booking-message">{bookingMessage}</p>}

      </div>
    </div>
  );
};

export default NewspaperLayout;
