import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { getNewspapers } from "../services/api";

const BookAd = () => {
  const [newspapers, setNewspapers] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchNewspapers = async () => {
      const data = await getNewspapers();
      setNewspapers(data);
    };
    fetchNewspapers();
  }, []);

  const handleSelectNewspaper = (paper) => {
    navigate("/ad-booking", { state: { newspaper: paper } });
  };

  return (
    <div>
      <h2>Book an Advertisement</h2>
      <h3>Available Newspapers:</h3>
      {newspapers.length > 0 ? (
        newspapers.map((paper) => (
          <div key={paper.id} onClick={() => handleSelectNewspaper(paper)}>
            <h3>{paper.name} - Base Price: â‚¹{paper.price_per_word}</h3>
            <button>Book Now</button>
          </div>
        ))
      ) : (
        <p>No newspapers available.</p>
      )}
    </div>
  );
};

export default BookAd;
