import React, { useState, useEffect } from "react";
import axios from "axios";
import "../../styles/AdminAdManagement.css";

const AdminAdManagement = () => {
  const [adSpaces, setAdSpaces] = useState([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");

  // Fetch all ad spaces
  useEffect(() => {
    const fetchAdSpaces = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/ad-booking/ad-spaces/1");
        setAdSpaces(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching ad spaces:", error);
        setLoading(false);
      }
    };
    fetchAdSpaces();
  }, []);

  // Toggle ad space status
  const toggleAdSpaceStatus = async (spaceId, currentStatus) => {
    try {
      const newStatus = currentStatus === "available" ? "booked" : "available";
      await axios.put(`http://localhost:5000/api/ad-booking/ad-spaces/${spaceId}`, {
        status: newStatus
      });
      
      // Update local state
      setAdSpaces(adSpaces.map(space => 
        space.id === spaceId ? { ...space, status: newStatus } : space
      ));
      
      setMessage(`Ad space ${spaceId} updated to ${newStatus}`);
      setTimeout(() => setMessage(""), 3000);
    } catch (error) {
      console.error("Error updating ad space:", error);
      setMessage("Error updating ad space");
    }
  };

  if (loading) return <div>Loading ad spaces...</div>;

  return (
    <div className="admin-ad-management">
      <h2>Ad Space Management</h2>
      <p>Manage availability of ad spaces in The Virtual Times</p>
      
      {message && <div className="alert-message">{message}</div>}

      <div className="ad-spaces-list">
        <h3>Front Page Ad Spaces</h3>
        <div className="ad-space-grid">
          {adSpaces.map((space) => (
            <div key={space.id} className="admin-ad-space">
              <h4>{space.space_name}</h4>
              <p>Current Status: 
                <span className={`status-${space.status}`}>
                  {space.status}
                </span>
              </p>
              <p>Price: â‚¹{space.price}</p>
              <button
                onClick={() => toggleAdSpaceStatus(space.id, space.status)}
                className={`toggle-btn ${space.status === "available" ? "book-btn" : "free-btn"}`}
              >
                {space.status === "available" ? "Mark as Booked" : "Mark as Available"}
              </button>
            </div>
          ))}
        </div>
      </div>

      <div className="layout-preview">
        <h3>Newspaper Layout Preview</h3>
        <div className="preview-container">
          {/* This mimics your NewspaperLayout component for admin preview */}
          <div className="preview-newspaper">
            <h4>THE VIRTUAL TIMES</h4>
            <div className="preview-ad-spaces">
              {adSpaces.map((space) => (
                <div 
                  key={space.id}
                  className={`preview-ad ${space.status === "booked" ? "booked" : ""}`}
                >
                  {space.space_name}
                  <span>{space.status === "booked" ? "âœ” Booked" : "Available"}</span>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminAdManagement;
