import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../../styles/NewspaperLayout.css";

const NewspaperLayout = () => {
  const [selectedAd, setSelectedAd] = useState(null);
  const [selectedPrice, setSelectedPrice] = useState(null);
  const [selectedSize, setSelectedSize] = useState("");
  const [adSpaces, setAdSpaces] = useState([]);
  const navigate = useNavigate();

  const adPrices = {
    large: 5000,
    medium: 3000,
    small: 1500,
  };

  // Map of ad space positions to their expected names in the database
  const adSpacePositions = [
    { name: "Exclusive Front Page Ad Space", size: "large" },
    { name: "Premium Half-Page Ad", size: "medium" },
    { name: "Quarter-Page Ad", size: "small" },
    { name: "Prime Full-Width Ad Spot", size: "large" }
  ];

  useEffect(() => {
    const fetchAdSpaces = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/ad-booking/ad-spaces/1");
        setAdSpaces(response.data);
      } catch (error) {
        console.error("Error fetching ad spaces:", error);
      }
    };
    fetchAdSpaces();
  }, []);

  const handleAdClick = (positionIndex, size) => {
    const space = adSpaces.find(ad => 
      ad.space_name === adSpacePositions[positionIndex].name
    );
    
    if (space?.status === "booked") return;
    
    setSelectedAd(space.id); // Use the actual space ID from database
    setSelectedPrice(adPrices[size]);
    setSelectedSize(size);
  };

  const handleAdBooking = () => {
    if (!selectedAd) return;
    
    const selectedSpace = adSpaces.find(ad => ad.id === selectedAd);
    
    navigate("/deccan-chronicles-booking", {
      state: {
        size: selectedSize,
        price: selectedPrice,
        newspaperId: 1,
        newspaperName: "The Virtual Times",
        spaceId: selectedAd, // Use the actual space ID
        spaceName: selectedSpace.space_name
      },
    });
  };

  // Get status for a specific position
  const getAdStatus = (positionIndex) => {
    const expectedName = adSpacePositions[positionIndex].name;
    return adSpaces.find(ad => ad.space_name === expectedName) || { status: "available" };
  };

  return (
    <div className="newspaper-layout">
      <div className="newspaper-container">
        <h1 className="newspaper-title">THE VIRTUAL TIMES</h1>
        <p className="sub-title">Your Trusted Daily Source</p>
        <div className="separator"></div>

        <div className="main-headline">
          <h2>Pakistan's FIA to Collaborate with CBI on Fighting Terror</h2>
          <p>
            Intense cross-border security discussions are underway to curb
            international crime and ensure stability...
          </p>
        </div>

        {/* First Ad Section */}
        <div className="ads-container">
          <div
            className={`ad-space large ${getAdStatus(0).status === "booked" ? "disabled" : ""}`}
            onClick={() => handleAdClick(0, "large")}
          >
            ðŸ”¹ <strong>Exclusive Front Page Ad Space</strong> ðŸ”¹
            {getAdStatus(0).status === "booked" && <p>Booked</p>}
            {selectedAd === adSpaces.find(ad => ad.space_name === adSpacePositions[0].name)?.id && (
              <div>
                <p className="ad-price">Price: â‚¹{selectedPrice}</p>
                <button className="book-ad-button" onClick={handleAdBooking}>
                  Book Ad
                </button>
              </div>
            )}
          </div>
        </div>

        {/* Second Ad Section */}
        <div className="ads-container">
          <div
            className={`ad-space medium ${getAdStatus(1).status === "booked" ? "disabled" : ""}`}
            onClick={() => handleAdClick(1, "medium")}
          >
            ðŸ”¹ <strong>Premium Half-Page Ad</strong> ðŸ”¹
            {getAdStatus(1).status === "booked" && <p>Booked</p>}
            {selectedAd === adSpaces.find(ad => ad.space_name === adSpacePositions[1].name)?.id && (
              <div>
                <p className="ad-price">Price: â‚¹{selectedPrice}</p>
                <button className="book-ad-button" onClick={handleAdBooking}>
                  Book Ad
                </button>
              </div>
            )}
          </div>

          <div
            className={`ad-space small ${getAdStatus(2).status === "booked" ? "disabled" : ""}`}
            onClick={() => handleAdClick(2, "small")}
          >
            ðŸ”¹ <strong>Quarter-Page Ad</strong> ðŸ”¹
            {getAdStatus(2).status === "booked" && <p>Booked</p>}
            {selectedAd === adSpaces.find(ad => ad.space_name === adSpacePositions[2].name)?.id && (
              <div>
                <p className="ad-price">Price: â‚¹{selectedPrice}</p>
                <button className="book-ad-button" onClick={handleAdBooking}>
                  Book Ad
                </button>
              </div>
            )}
          </div>
        </div>

        {/* Fourth Ad Section */}
        <div className="ads-container">
          <div
            className={`ad-space large ${getAdStatus(3).status === "booked" ? "disabled" : ""}`}
            onClick={() => handleAdClick(3, "large")}
          >
            ðŸ”¹ <strong>Prime Full-Width Ad Spot</strong> ðŸ”¹
            {getAdStatus(3).status === "booked" && <p>Booked</p>}
            {selectedAd === adSpaces.find(ad => ad.space_name === adSpacePositions[3].name)?.id && (
              <div>
                <p className="ad-price">Price: â‚¹{selectedPrice}</p>
                <button className="book-ad-button" onClick={handleAdBooking}>
                  Book Ad
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default NewspaperLayout;
