import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import axios from "axios";
import "../../styles/NewspaperLayout.css";

const NewspaperLayout = () => {
  const [selectedAd, setSelectedAd] = useState(null);
  const [selectedPrice, setSelectedPrice] = useState(null);
  const [selectedSize, setSelectedSize] = useState("");
  const [bookedSpaces, setBookedSpaces] = useState([]);
  const navigate = useNavigate();
  const location = useLocation();

  const newspaperId = location.state?.newspaperId || 1; // Default to 1

  // Prices for different ad sizes
  const adPrices = {
    large: 5000,
    medium: 3000,
    small: 1500,
  };

  // Fetch booked ad spaces
  useEffect(() => {
    const fetchBookedSpaces = async () => {
      try {
        const response = await axios.get(
          `http://localhost:5000/api/ad-spaces/${newspaperId}`
        );
        const booked = response.data
          .filter((space) => space.status === "booked")
          .map((space) => space.id);
        setBookedSpaces(booked);
      } catch (error) {
        console.error("Error fetching ad spaces:", error);
      }
    };

    fetchBookedSpaces();
  }, [newspaperId]);

  // Handle ad selection
  const handleAdClick = (size, index, spaceId) => {
    if (bookedSpaces.includes(spaceId)) return; // Prevent selection

    setSelectedAd(index);
    setSelectedPrice(adPrices[size]);
    setSelectedSize(size);
  };

  // Handle ad booking
  const handleAdBooking = () => {
    navigate("/deccan-chronicles-booking", {
      state: {
        size: selectedSize,
        price: selectedPrice,
        newspaperId,
        newspaperName: "The Virtual Times",
        spaceId: selectedAd + 1,
      },
    });
  };

  return (
    <div className="newspaper-layout">
      <div className="newspaper-container">
        <h1 className="newspaper-title">THE VIRTUAL TIMES</h1>
        <p className="sub-title">Your Trusted Daily Source</p>
        <div className="separator"></div>

        <div className="main-headline">
          <h2>Pakistanâ€™s FIA to Collaborate with CBI on Fighting Terror</h2>
          <p>Intense cross-border security discussions are underway...</p>
        </div>

        {/* Ad Spaces */}
        <div className="ads-container">
          {[{ size: "large", id: 1 }, { size: "medium", id: 2 }, { size: "small", id: 3 }].map(
            (ad, index) => (
              <div
                key={ad.id}
                className={`ad-space ${ad.size} ${bookedSpaces.includes(ad.id) ? "booked" : ""}`}
                onClick={() => handleAdClick(ad.size, index, ad.id)}
                style={{ cursor: bookedSpaces.includes(ad.id) ? "not-allowed" : "pointer" }}
              >
                ðŸ”¹ <strong>{ad.size.toUpperCase()} Ad Space</strong> ðŸ”¹
                {selectedAd === index && !bookedSpaces.includes(ad.id) && (
                  <div>
                    <p className="ad-price">Price: â‚¹{selectedPrice}</p>
                    <button className="book-ad-button" onClick={handleAdBooking}>
                      Book Ad
                    </button>
                  </div>
                )}
                {bookedSpaces.includes(ad.id) && <p className="booked-label">Booked</p>}
              </div>
            )
          )}
        </div>
      </div>
    </div>
  );
};

export default NewspaperLayout;
