import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { getNewspapers, bookAd } from "../serviceS/api";

const BookAd = () => {
  const navigate = useNavigate();
  const [newspapers, setNewspapers] = useState([]);
  const [selectedNewspaper, setSelectedNewspaper] = useState(null);
  const [adContent, setAdContent] = useState("");
  const [price, setPrice] = useState(0);

  useEffect(() => {
    const fetchNewspapers = async () => {
      const data = await getNewspapers();
      setNewspapers(data);
    };
    fetchNewspapers();
  }, []);

  const handleNewspaperSelect = (paper) => {
    setSelectedNewspaper(paper);
    setPrice(0); // Reset price when newspaper changes
  };

  const handleContentChange = (e) => {
    setAdContent(e.target.value);
    if (selectedNewspaper) {
      const wordCount = e.target.value.trim().split(/\s+/).length;
      setPrice(wordCount * selectedNewspaper.price_per_word);
    }
  };

  const handleBooking = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");
    if (!token) {
      alert("Please log in to book an ad.");
      navigate("/login");
      return;
    }

    try {
      const adData = {
        newspaper_id: selectedNewspaper.id,
        content: adContent,
        price,
      };
      const response = await bookAd(adData);
      alert(response.message);
      navigate("/");
    } catch (error) {
      alert("Failed to book ad. Try again.");
    }
  };

  return (
    <div>
      <h1>Book an Ad</h1>
      <h2>Select a Newspaper:</h2>
      <ul>
        {newspapers.map((paper) => (
          <li key={paper.id} onClick={() => handleNewspaperSelect(paper)}>
            {paper.name} - ₹{paper.price_per_word} per word
          </li>
        ))}
      </ul>
      {selectedNewspaper && (
        <form onSubmit={handleBooking}>
          <h3>Selected: {selectedNewspaper.name}</h3>
          <textarea
            placeholder="Enter ad content..."
            value={adContent}
            onChange={handleContentChange}
            required
          />
          <p>Total Price: ₹{price}</p>
          <button type="submit">Book Ad</button>
        </form>
      )}
    </div>
  );
};

export default BookAd;
