import axios from "axios";

const API_BASE_URL = "http://localhost:5000/api";

// ✅ Fetch newspapers
export const getNewspapers = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/newspapers`); // Fixed syntax
    return response.data; // Ensure it returns an array
  } catch (error) {
    console.error("❌ Fetch Newspapers Error:", error);
    return [];
  }
};

// ✅ Login API
export const loginUser = async (email, password) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/auth/login`, { email, password }); // Fixed syntax
    return response.data;
  } catch (error) {
    console.error("❌ Login Error:", error.response?.data || error.message);
    throw error;
  }
};

// ✅ Signup API
export const signupUser = async (userData) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/auth/signup`, userData); // Fixed syntax
    return response.data;
  } catch (error) {
    console.error("❌ Signup Error:", error.response?.data || error.message);
    throw error;
  }
};

// ✅ Get Authenticated User
export const getAuthenticatedUser = () => {
  const token = localStorage.getItem("token");
  return token ? JSON.parse(atob(token.split(".")[1])) : null;
};
