import React, { useState, useEffect } from "react";
import { getNewspapers } from "../services/api";

function Explore() {
  const [newspapers, setNewspapers] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchNewspapers = async () => {
      try {
        setIsLoading(true);
        const data = await getNewspapers();
        if (Array.isArray(data)) {
          setNewspapers(data);
        } else {
          setNewspapers([]);
        }
      } catch (error) {
        console.error("Error fetching newspapers:", error);
        setNewspapers([]);
      } finally {
        setIsLoading(false);
      }
    };
    fetchNewspapers();
  }, []);

  return (
    <div style={styles.container}>
      <div style={styles.overlay}></div>
      <div style={styles.content}>
        <h1 style={styles.title}>Explore Newspapers</h1>
        <p style={styles.subtitle}>Browse our comprehensive collection of newspapers for your advertising needs</p>
        
        {isLoading ? (
          <div style={styles.loadingContainer}>
            <div style={styles.spinner}></div>
            <p style={styles.loadingText}>Loading newspapers...</p>
          </div>
        ) : newspapers.length > 0 ? (
          <div style={styles.grid}>
            {newspapers.map((paper) => (
              <div key={paper.id} style={styles.card}>
                <div style={styles.cardHeader}>
                  <h3 style={styles.paperName}>{paper.name}</h3>
                </div>
                <div style={styles.cardBody}>
                  <div style={styles.priceContainer}>
                    <span style={styles.priceLabel}>Price:</span>
                    <span style={styles.price}>â‚¹{paper.price_per_word} per word</span>
                  </div>
                  <button style={styles.viewButton}>View Details</button>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div style={styles.emptyState}>
            <p style={styles.emptyText}>No newspapers available at the moment.</p>
          </div>
        )}
      </div>
    </div>
  );
}

const styles = {
  container: {
    position: "relative",
    minHeight: "100vh",
    backgroundImage: "url('https://images.unsplash.com/photo-1585829365295-ab7cd400c7e9?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80')",
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundAttachment: "fixed",
    fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, sans-serif",
  },
  overlay: {
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "rgba(255, 255, 255, 0.85)",
    zIndex: 1,
  },
  content: {
    position: "relative",
    zIndex: 2,
    padding: "40px 20px",
    maxWidth: "1200px",
    margin: "0 auto",
  },
  title: {
    fontSize: "2.5rem",
    fontWeight: "700",
    color: "#2d3748",
    marginBottom: "10px",
    textAlign: "center",
    background: "linear-gradient(90deg, #2b6cb0, #4299e1)",
    WebkitBackgroundClip: "text",
    WebkitTextFillColor: "transparent",
  },
  subtitle: {
    fontSize: "1.1rem",
    color: "#4a5568",
    textAlign: "center",
    marginBottom: "40px",
    maxWidth: "700px",
    marginLeft: "auto",
    marginRight: "auto",
  },
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(300px, 1fr))",
    gap: "30px",
    marginTop: "30px",
  },
  card: {
    backgroundColor: "white",
    borderRadius: "12px",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.05)",
    overflow: "hidden",
    transition: "all 0.3s ease",
  },
  cardHeader: {
    backgroundColor: "#2b6cb0",
    padding: "20px",
    color: "white",
  },
  paperName: {
    fontSize: "1.3rem",
    fontWeight: "600",
    margin: 0,
  },
  cardBody: {
    padding: "20px",
  },
  priceContainer: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: "20px",
  },
  priceLabel: {
    color: "#718096",
    fontSize: "0.9rem",
  },
  price: {
    color: "#2b6cb0",
    fontWeight: "600",
    fontSize: "1.1rem",
  },
  viewButton: {
    width: "100%",
    padding: "12px",
    backgroundColor: "#4299e1",
    color: "white",
    border: "none",
    borderRadius: "6px",
    fontSize: "1rem",
    fontWeight: "500",
    cursor: "pointer",
    transition: "all 0.2s ease",
  },
  viewButtonHover: {
    backgroundColor: "#3182ce",
    transform: "translateY(-1px)",
  },
  loadingContainer: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    padding: "40px",
  },
  spinner: {
    width: "50px",
    height: "50px",
    border: "4px solid rgba(66, 153, 225, 0.2)",
    borderTopColor: "#4299e1",
    borderRadius: "50%",
    animation: "spin 1s linear infinite",
    marginBottom: "20px",
  },
  loadingText: {
    color: "#4a5568",
    fontSize: "1.1rem",
  },
  emptyState: {
    textAlign: "center",
    padding: "40px",
    backgroundColor: "rgba(255, 255, 255, 0.7)",
    borderRadius: "8px",
    maxWidth: "500px",
    margin: "0 auto",
  },
  emptyText: {
    color: "#4a5568",
    fontSize: "1.1rem",
  },
};

// Animation for spinner
const styleElement = document.createElement('style');
styleElement.textContent = `
  @keyframes spin {
    to { transform: rotate(360deg); }
  }
`;
document.head.appendChild(styleElement);

export default Explore;
