import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../../styles/NewspaperLayout.css";

const NewspaperLayout = () => {
  const [selectedAd, setSelectedAd] = useState(null);
  const [selectedPrice, setSelectedPrice] = useState(null);
  const [selectedSize, setSelectedSize] = useState("");
  const [adSpaces, setAdSpaces] = useState([]);
  const navigate = useNavigate();

  // ✅ Fixed ad prices (ensure consistent pricing)
  const adPrices = {
    large: 5000,
    medium: 3000,
    small: 1500,
  };

  // ✅ Fetch ad spaces from the backend
  useEffect(() => {
    const fetchAdSpaces = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/ad-booking/ad-spaces/1");
        setAdSpaces(response.data);
      } catch (error) {
        console.error("Error fetching ad spaces:", error);
      }
    };
    fetchAdSpaces();
  }, []);

  // ✅ Handle ad space selection
  const handleAdClick = (size, position) => {
    const ad = adSpaces.find((ad) => ad.position === position);
    if (ad?.status === "booked") return; // Prevent selecting booked ads

    setSelectedAd(position);
    setSelectedPrice(adPrices[size]);
    setSelectedSize(size);
  };

  // ✅ Navigate to ad booking page
  const handleAdBooking = () => {
    navigate("/deccan-chronicles-booking", {
      state: {
        size: selectedSize,
        price: selectedPrice,
        newspaperId: 1,
        newspaperName: "The Virtual Times",
        spaceId: selectedAd,
      },
    });
  };

  // ✅ Helper to get ad status by position
  const getAdStatus = (position) => {
    return adSpaces.find((ad) => ad.position === position) || { status: "available" };
  };

  return (
    <div className="newspaper-layout">
      <div className="newspaper-container">
        <h1 className="newspaper-title">THE VIRTUAL TIMES</h1>
        <p className="sub-title">Your Trusted Daily Source</p>
        <div className="separator"></div>

        {/* Main Headline */}
        <div className="main-headline">
          <h2>Pakistan’s FIA to Collaborate with CBI on Fighting Terror</h2>
          <p>Intense cross-border security discussions are underway to curb international crime and ensure stability...</p>
        </div>

        {/* First Ad Section (Position 1) */}
        <div className="ads-container">
          {(() => {
            const ad = getAdStatus(1);
            return (
              <div
                className={`ad-space large ${ad.status === "booked" ? "disabled" : ""}`}
                onClick={() => handleAdClick("large", 1)}
              >
                🔹 <strong>Exclusive Front Page Ad Space</strong> 🔹
                {ad.status === "booked" && <p>Booked</p>}
                {selectedAd === 1 && ad.status !== "booked" && (
                  <div>
                    <p className="ad-price">Price: ₹{selectedPrice}</p>
                    <button className="book-ad-button" onClick={handleAdBooking}>Book Ad</button>
                  </div>
                )}
              </div>
            );
          })()}
        </div>

        {/* Middle Ad Section (Position 2 and 3) */}
        <div className="ads-container">
          {(() => {
            const ad2 = getAdStatus(2);
            const ad3 = getAdStatus(3);
            return (
              <>
                <div
                  className={`ad-space medium ${ad2.status === "booked" ? "disabled" : ""}`}
                  onClick={() => handleAdClick("medium", 2)}
                >
                  🔹 <strong>Premium Half-Page Ad</strong> 🔹
                  {ad2.status === "booked" && <p>Booked</p>}
                  {selectedAd === 2 && ad2.status !== "booked" && (
                    <div>
                      <p className="ad-price">Price: ₹{selectedPrice}</p>
                      <button className="book-ad-button" onClick={handleAdBooking}>Book Ad</button>
                    </div>
                  )}
                </div>

                <div
                  className={`ad-space small ${ad3.status === "booked" ? "disabled" : ""}`}
                  onClick={() => handleAdClick("small", 3)}
                >
                  🔹 <strong>Quarter-Page Ad</strong> 🔹
                  {ad3.status === "booked" && <p>Booked</p>}
                  {selectedAd === 3 && ad3.status !== "booked" && (
                    <div>
                      <p className="ad-price">Price: ₹{selectedPrice}</p>
                      <button className="book-ad-button" onClick={handleAdBooking}>Book Ad</button>
                    </div>
                  )}
                </div>
              </>
            );
          })()}
        </div>

        {/* Bottom Ad Section (Position 4) */}
        <div className="ads-container">
          {(() => {
            const ad4 = getAdStatus(4);
            return (
              <div
                className={`ad-space large ${ad4.status === "booked" ? "disabled" : ""}`}
                onClick={() => handleAdClick("large", 4)}
              >
                🔹 <strong>Prime Full-Width Ad Spot</strong> 🔹
                {ad4.status === "booked" && <p>Booked</p>}
                {selectedAd === 4 && ad4.status !== "booked" && (
                  <div>
                    <p className="ad-price">Price: ₹{selectedPrice}</p>
                    <button className="book-ad-button" onClick={handleAdBooking}>Book Ad</button>
                  </div>
                )}
              </div>
            );
          })()}
        </div>
      </div>
    </div>
  );
};

export default NewspaperLayout;
