import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom"; // ✅ Import navigation
import { getNewspapers } from "../services/api"; 

const BookAd = () => {
  const [newspapers, setNewspapers] = useState([]);
  const navigate = useNavigate(); // ✅ Initialize navigation

  useEffect(() => {
    const fetchNewspapers = async () => {
      const data = await getNewspapers();
      setNewspapers(data);
    };
    fetchNewspapers();
  }, []);

  // ✅ Function to navigate to AdBooking.js when a newspaper is selected
  const handleSelectNewspaper = (paper) => {
    navigate(`/ad-booking/${paper.id}`, { state: { newspaper: paper } });
  };

  return (
    <div>
      <h2>Book an Advertisement</h2>
      <h3>Available Newspapers:</h3>
      {newspapers.length > 0 ? (
        newspapers.map((paper) => (
          <div 
            key={paper.id} 
            style={{ 
              cursor: "pointer", 
              border: "1px solid #ccc", 
              padding: "10px", 
              margin: "10px" 
            }}
            onClick={() => handleSelectNewspaper(paper)}
          >
            <h3>{paper.name} - Base Price: ₹{paper.price_per_word}</h3>
            <p>Click to select and view available ad spaces.</p>
          </div>
        ))
      ) : (
        <p>No newspapers available.</p>
      )}
    </div>
  );
};

export default BookAd;
