import React, { useState, useEffect } from "react";
import { getNewspapers } from "../services/api";

function Explore() {
  // EXISTING STATE MANAGEMENT - UNCHANGED
  const [newspapers, setNewspapers] = useState([]);

  // EXISTING DATA FETCHING LOGIC - UNCHANGED
  useEffect(() => {
    const fetchNewspapers = async () => {
      const data = await getNewspapers();
      if (Array.isArray(data)) {
        setNewspapers(data);
      } else {
        setNewspapers([]);
      }
    };
    fetchNewspapers();
  }, []);

  // ENHANCED STYLING ONLY
  const styles = {
    container: {
      position: "relative",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center",
      padding: "20px",
      backgroundImage: "url('https://cdn.dribbble.com/users/2321513/screenshots/17411847/media/d773265730da159098d1b48b8714df7f.png?resize=1000x750&vertical=center')",
      backgroundSize: "cover",
      backgroundPosition: "center",
      minHeight: "100vh",
      fontFamily: "'Arial', sans-serif",
    },
    overlay: {
      position: "absolute",
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: "rgba(255, 255, 255, 0.4)",
      zIndex: 1,
    },
    title: {
      fontSize: "2.5rem",
      marginBottom: "40px",
      position: "relative",
      zIndex: 2,
      color: "#143b88",
      textShadow: "1px 1px 3px rgba(0,0,0,0.1)",
    },
    list: {
      listStyleType: "none",
      padding: 0,
      width: "100%",
      maxWidth: "800px",
      margin: "0 auto",
      position: "relative",
      zIndex: 2,
    },
    listItem: {
      backgroundColor: "rgba(255, 255, 255, 0.95)",
      border: "1px solid #e2e8f0",
      borderRadius: "8px",
      padding: "20px 25px",
      margin: "15px 0",
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      boxShadow: "0 2px 8px rgba(0, 0, 0, 0.08)",
      transition: "all 0.2s ease",
    },
    paperName: {
      fontWeight: "bold",
      color: "#2567c5",
      fontSize: "1.1rem",
    },
    price: {
      color: "#2e7d32",
      fontWeight: "bold",
      fontSize: "1rem",
    },
    loading: {
      fontSize: "1.2rem",
      color: "#2d3748",
      position: "relative",
      zIndex: 2,
      backgroundColor: "rgba(255,255,255,0.8)",
      padding: "10px 20px",
      borderRadius: "6px",
    },
  };

  return (
    <div style={styles.container}>
      <div style={styles.overlay}></div>
      <h1 style={styles.title}>Explore Newspapers</h1>
      {newspapers.length > 0 ? (
        <ul style={styles.list}>
          {newspapers.map((paper) => (
            <li key={paper.id} style={styles.listItem}>
              <span style={styles.paperName}>{paper.name}</span>
              <span style={styles.price}>â‚¹{paper.price_per_word} per word</span>
            </li>
          ))}
        </ul>
      ) : (
        <p style={styles.loading}>Loading newspapers...</p>
      )}
    </div>
  );
}

export default Explore;
