import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../../styles/NewspaperLayout.css";

const NewspaperLayout = () => {
  const [selectedAd, setSelectedAd] = useState(null);
  const [selectedPrice, setSelectedPrice] = useState(null);
  const [selectedSize, setSelectedSize] = useState("");
  const [adSpaces, setAdSpaces] = useState([]);
  const navigate = useNavigate();

  // Prices for different ad sizes (fallback if not provided by backend)
  const adPrices = {
    large: 5000,
    medium: 3000,
    small: 1500,
  };

  // âœ… Fetch available and booked ad spaces from the backend
  useEffect(() => {
    const fetchAdSpaces = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/ad-spaces/1"); // Ensure the correct newspaperId
        if (!response.ok) throw new Error("Failed to fetch ad spaces");
        const data = await response.json();
        setAdSpaces(data);
      } catch (error) {
        console.error("Error fetching ad spaces:", error);
      }
    };
    fetchAdSpaces();
  }, []);

  // âœ… Handle ad selection (disable if booked)
  const handleAdClick = (size, index, status) => {
    if (status === "booked") return; // Prevent selecting booked spaces
    setSelectedAd(index);
    setSelectedPrice(adPrices[size]);
    setSelectedSize(size);
  };

  // âœ… Navigate to ad booking page
  const handleAdBooking = () => {
    navigate("/deccan-chronicles-booking", {
      state: {
        size: selectedSize,
        price: selectedPrice,
        newspaperId: 1,
        newspaperName: "The Virtual Times",
        spaceId: selectedAd + 1,
      },
    });
  };

  return (
    <div className="newspaper-layout">
      <div className="newspaper-container">
        <h1 className="newspaper-title">THE VIRTUAL TIMES</h1>
        <p className="sub-title">Your Trusted Daily Source</p>
        <div className="separator"></div>

        <div className="main-headline">
          <h2>Pakistanâ€™s FIA to Collaborate with CBI on Fighting Terror</h2>
          <p>Intense cross-border security discussions are underway to curb international crime and ensure stability...</p>
        </div>

        {/* âœ… Dynamic Ad Spaces with Booking Check */}
        <div className="ads-container">
          {adSpaces.map((ad, index) => (
            <div
              key={index}
              className={`ad-space ${ad.size} ${ad.status === "booked" ? "disabled" : ""}`}
              onClick={() => handleAdClick(ad.size, index, ad.status)}
            >
              ðŸ”¹ <strong>{ad.size.toUpperCase()} Ad Space</strong> ðŸ”¹
              {ad.status === "booked" && <p className="booked-text">Booked</p>}
              {selectedAd === index && ad.status !== "booked" && (
                <div>
                  <p className="ad-price">Price: â‚¹{selectedPrice}</p>
                  <button className="book-ad-button" onClick={handleAdBooking}>Book Ad</button>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default NewspaperLayout;
