export const getUserData = async () => {
  const token = getToken();
  console.log("üîç Token being sent:", token); // ‚úÖ Check token before API call

  if (!token) {
    console.warn("‚ùå No token found in localStorage. User is not logged in.");
    return null; // ‚úÖ Prevent API call if no token
  }

  try {
    const response = await axios.get(`${API_BASE_URL}/auth/user`, {
      headers: {
        Authorization: `Bearer ${token}`, // ‚úÖ Include token in header
        "Content-Type": "application/json",
      },
    });
    console.log("‚úÖ User Data Response:", response.data); // ‚úÖ Log successful response
    return response.data;
  } catch (error) {
    console.error("‚ùå User Fetch Error:", error.response?.data || error.message);

    // ‚úÖ Handle 401 Unauthorized (Invalid Token)
    if (error.response?.status === 401) {
      console.warn("üö® Invalid token detected. Logging out...");
      localStorage.removeItem("token");
    }

    return null;
  }
};
