import { useState, useEffect } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext"; // Ensure authentication context is used

const AdBooking = () => {
  const { user } = useAuth(); // Get user authentication info
  const [newspapers, setNewspapers] = useState([]);
  const [selectedNewspaper, setSelectedNewspaper] = useState(null);
  const [adSpaces, setAdSpaces] = useState([]);
  const [selectedAdSpace, setSelectedAdSpace] = useState(null);
  const [content, setContent] = useState("");
  const [price, setPrice] = useState(null);

  useEffect(() => {
    // Fetch newspapers
    axios.get("http://localhost:5000/api/newspapers")
      .then(response => setNewspapers(response.data))
      .catch(error => console.error("Error fetching newspapers:", error));
  }, []);

  const handleNewspaperSelect = async (newspaperId) => {
    setSelectedNewspaper(newspaperId);
    setSelectedAdSpace(null);
    setPrice(null);

    try {
      const response = await axios.get(`http://localhost:5000/api/ad-spaces/${newspaperId}`);
      setAdSpaces(response.data);
    } catch (error) {
      console.error("Error fetching ad spaces:", error);
    }
  };

  const handleAdSpaceSelect = (space) => {
    setSelectedAdSpace(space);
    setPrice(space.price);
  };

  const handleBooking = async () => {
    if (!user) {
      alert("Please log in to book an ad.");
      return;
    }

    if (!selectedNewspaper || !selectedAdSpace || !content) {
      alert("Please fill all fields before booking.");
      return;
    }

    try {
      await axios.post(
        "http://localhost:5000/api/book-ad",
        {
          newspaperId: selectedNewspaper,
          spaceId: selectedAdSpace.id,
          content,
          price,
          email: user.email
        },
        {
          headers: { Authorization: `Bearer ${user.token}` }
        }
      );
      alert("Ad booked successfully!");
      handleNewspaperSelect(selectedNewspaper); // Refresh ad spaces after booking
    } catch (error) {
      console.error("Error booking ad:", error);
      alert("Booking failed. Please try again.");
    }
  };

  return (
    <div>
      <h2>Book an Ad</h2>
      <label>Select Newspaper:</label>
      <select value={selectedNewspaper || ""} onChange={(e) => handleNewspaperSelect(e.target.value)}>
        <option value="">-- Select --</option>
        {newspapers.map(n => (
          <option key={n.id} value={n.id}>{n.name}</option>
        ))}
      </select>

      {selectedNewspaper && (
        <>
          <h3>Available Ad Spaces:</h3>
          {adSpaces.length === 0 ? (
            <p>No ad spaces available for this newspaper.</p>
          ) : (
            <ul>
              {adSpaces.map(space => (
                <li key={space.id} onClick={() => handleAdSpaceSelect(space)} style={{ cursor: 'pointer', background: selectedAdSpace?.id === space.id ? '#cce5ff' : 'transparent' }}>
                  {space.space_name} - ${space.price} ({space.status})
                </li>
              ))}
            </ul>
          )}
        </>
      )}

      {selectedAdSpace && (
        <>
          <h3>Enter Ad Content:</h3>
          <textarea value={content} onChange={(e) => setContent(e.target.value)}></textarea>
          <p>Price: ${price}</p>
          <button onClick={handleBooking}>Book Ad</button>
        </>
      )}
    </div>
  );
};

export default AdBooking;
