// src/pages/BookAd.js

import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import NewspaperLayout from "./layouts/NewspaperLayout";


const BookAd = () => {
  const location = useLocation();
  const { newspaperId, newspaperName, spaceId } = location.state || {};

  const [selectedNewspaper, setSelectedNewspaper] = useState(newspaperName || "");
  const [adContent, setAdContent] = useState("");

  const handleNewspaperChange = (event) => {
    setSelectedNewspaper(event.target.value);
  };

  const calculatePrice = () => {
    if (!selectedNewspaper) {
      alert("Please select a newspaper first.");
      return;
    }
    const pricePerWord = selectedNewspaper === "The Virtual Times" ? 10 : 8;
    const wordCount = adContent.split(" ").filter((word) => word).length;
    const totalPrice = pricePerWord * wordCount;
    alert(`Total Price: ₹${totalPrice}`);
  };

  const proceedToPayment = () => {
    if (!selectedNewspaper || !adContent.trim()) {
      alert("Please select a newspaper and enter ad content before proceeding.");
      return;
    }
    alert(`Proceeding to payment with:\nNewspaper ID: ${newspaperId}\nNewspaper: ${selectedNewspaper}\nSpace ID: ${spaceId}`);
  };

  return (
    <div style={styles.bookAdContainer}>
      <div style={styles.bookAdBox}>
        <h2>Book Your Ad</h2>

        <label>Select a Newspaper:</label>
        <select
          onChange={handleNewspaperChange}
          value={selectedNewspaper}
          style={styles.newspaperSelect}
        >
          <option value="">-- Select --</option>
          <option value="The Virtual Times">The Virtual Times - ₹10 per word</option>
          <option value="Daily News">Daily News - ₹8 per word</option>
        </select>

        {selectedNewspaper && (
          <div style={styles.layoutContainer}>
            <NewspaperLayout name={selectedNewspaper} />
          </div>
        )}

        <label>Enter Ad Content:</label>
        <textarea
          placeholder="Type your ad content here..."
          value={adContent}
          onChange={(e) => setAdContent(e.target.value)}
          style={styles.textArea}
        />

        <div style={styles.buttonContainer}>
          <button onClick={calculatePrice} style={styles.button}>
            Calculate Price
          </button>
          <button onClick={proceedToPayment} style={styles.button}>
            Proceed to Payment
          </button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  bookAdContainer: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    backgroundColor: "#f4f4f4",
    margin: 0,
    padding: 0,
    boxSizing: "border-box",
  },
  bookAdBox: {
    width: "400px",
    padding: "30px",
    background: "white",
    borderRadius: "12px",
    boxShadow: "0 8px 16px rgba(0, 0, 0, 0.1)",
    display: "flex",
    flexDirection: "column",
    textAlign: "center",
  },
  newspaperSelect: {
    margin: "15px 0",
    padding: "12px",
    width: "50%", // Reduced the width of the dropdown
    border: "1px solid #ccc",
    borderRadius: "8px",
    boxSizing: "border-box",
    alignSelf: "center", // Centered the dropdown
  },
  layoutContainer: {
    margin: "20px 0",
  },
  textArea: {
    padding: "12px",
    border: "1px solid #ccc",
    borderRadius: "8px",
    minHeight: "100px",
    marginTop: "15px",
  },
  buttonContainer: {
    marginTop: "20px",
    display: "flex",
    justifyContent: "space-between",
  },
  button: {
    backgroundColor: "#007bff",
    color: "white",
    border: "none",
    padding: "12px 20px",
    borderRadius: "8px",
    cursor: "pointer",
    transition: "background 0.3s ease",
  },
};

export default BookAd;
