import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../../styles/NewspaperLayout.css";
import axios from "axios";

const NewspaperLayout = () => {
  const [adSpaces, setAdSpaces] = useState([]);
  const [selectedAd, setSelectedAd] = useState(null);
  const [selectedPrice, setSelectedPrice] = useState(null);
  const [selectedSize, setSelectedSize] = useState("");
  const navigate = useNavigate();

  // Prices for different ad sizes
  const adPrices = {
    large: 5000,
    medium: 3000,
    small: 1500,
  };

  // Fetch ad spaces and their booking status
  useEffect(() => {
    const fetchAdSpaces = async () => {
      try {
        const response = await axios.get("/api/ad-spaces/1"); // Assuming newspaperId = 1
        setAdSpaces(response.data);
        console.log("Fetched ad spaces:", response.data);
      } catch (error) {
        console.error("Error fetching ad spaces:", error);
      }
    };
    fetchAdSpaces();
  }, []);

  // Handle ad selection
  const handleAdClick = (size, index) => {
    if (!adSpaces || adSpaces.length <= index) {
      console.error("Ad space not found at index:", index);
      return;
    }

    // Prevent booking of already booked spaces
    if (adSpaces[index].status === "booked") {
      alert("This ad space is already booked!");
      return;
    }

    setSelectedAd(index);
    setSelectedPrice(adPrices[size]);
    setSelectedSize(size);
  };

  // Navigate to ad booking page
  const handleAdBooking = () => {
    navigate("/deccan-chronicles-booking", {
      state: {
        size: selectedSize,
        price: selectedPrice,
        newspaperId: 1, // Replace with dynamic ID if needed
        newspaperName: "The Virtual Times",
        spaceId: selectedAd + 1, // Ensure each ad has a unique space ID
      },
    });
  };

  return (
    <div className="newspaper-layout">
      <div className="newspaper-container">
        <h1 className="newspaper-title">THE VIRTUAL TIMES</h1>
        <p className="sub-title">Your Trusted Daily Source</p>
        <div className="separator"></div>

        <div className="main-headline">
          <h2>Pakistanâ€™s FIA to Collaborate with CBI on Fighting Terror</h2>
          <p>
            Intense cross-border security discussions are underway to curb
            international crime and ensure stability...
          </p>
        </div>

        {/* Render Ad Spaces Dynamically */}
        <div className="ads-container">
          {["large", "medium", "small", "large"].map((size, index) => (
            <div
              key={index}
              className={`ad-space ${size} ${
                adSpaces[index]?.status === "booked" ? "booked" : ""
              }`}
              onClick={() => handleAdClick(size, index)}
            >
              ðŸ”¹ <strong>{size.toUpperCase()} Ad Space</strong> ðŸ”¹
              {adSpaces[index]?.status === "booked" && (
                <p className="booked-label">Booked</p>
              )}

              {selectedAd === index && adSpaces[index]?.status !== "booked" && (
                <div>
                  <p className="ad-price">Price: â‚¹{selectedPrice}</p>
                  <button
                    className="book-ad-button"
                    onClick={handleAdBooking}
                  >
                    Book Ad
                  </button>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default NewspaperLayout;
