import React, { useState } from "react";

const AdBooking = () => {
    const [price, setPrice] = useState(0);

    const handleContentChange = (event) => {
        const content = event.target.value;
        
        // Function to calculate adjusted price (Ensure it returns a number)
        const calculateAdjustedPrice = () => {
            let basePrice = 100; // Example base price
            let adjustedPrice = basePrice + content.length * 2; // Adjust price dynamically
            
            return isNaN(adjustedPrice) ? 0 : Number(adjustedPrice); // Ensure it's a number
        };

        const adjustedPrice = calculateAdjustedPrice();

        if (!isNaN(adjustedPrice)) {
            setPrice(adjustedPrice.toFixed(2));
        } else {
            console.error("Error: adjustedPrice is not a number:", adjustedPrice);
        }
    };

    return (
        <div>
            <h2>Ad Booking</h2>
            <textarea onChange={handleContentChange} placeholder="Enter your ad content..." />
            <p>Price: ${price}</p>
        </div>
    );
};

export default AdBooking;
