import React, { useState } from "react";

const BookAd = () => {
  const [selectedNewspaper, setSelectedNewspaper] = useState("");

  const handleNewspaperChange = (e) => {
    setSelectedNewspaper(e.target.value);
  };

  return (
    <div style={styles.bookAdContainer}>
      <div style={styles.bookAdBox}>
        <h2>Book Your Ad</h2>

        {/* Select Newspaper Dropdown */}
        <div style={styles.dropdownContainer}>
          <label htmlFor="newspaper">Select Newspaper:</label>
          <select
            id="newspaper"
            style={styles.newspaperSelect}
            onChange={handleNewspaperChange}
            value={selectedNewspaper}
          >
            <option value="">-- Select --</option>
            <option value="The Virtual Times">The Virtual Times - ₹10 per word</option>
            <option value="Daily News">Daily News - ₹8 per word</option>
          </select>
        </div>

        {/* Buttons */}
        <div style={styles.bookAdButtons}>
          <button style={styles.button}>Submit</button>
          <button style={styles.button}>Cancel</button>
        </div>
      </div>
    </div>
  );
};

// ✅ Inline Styles Object
const styles = {
  bookAdContainer: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh", // Full viewport height
    backgroundColor: "#f4f4f4",
    margin: "0",
    padding: "0",
    boxSizing: "border-box",
  },
  bookAdBox: {
    width: "400px", // Box width
    padding: "30px",
    background: "white",
    borderRadius: "12px",
    boxShadow: "0 8px 16px rgba(0, 0, 0, 0.1)",
    display: "flex",
    flexDirection: "column",
    alignItems: "center", // Center content horizontally
    textAlign: "center",
  },
  dropdownContainer: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center", // Center label and dropdown
    marginBottom: "20px",
    width: "100%", // Ensure full width for centering
  },
  newspaperSelect: {
    marginTop: "10px", // Space between label and dropdown
    padding: "12px",
    width: "70%", // ✅ Smaller dropdown width
    border: "1px solid #ccc",
    borderRadius: "8px",
    boxSizing: "border-box",
  },
  bookAdButtons: {
    marginTop: "20px",
    display: "flex",
    justifyContent: "space-between",
    width: "100%", // Ensure buttons are spread out
  },
  button: {
    backgroundColor: "#007bff",
    color: "white",
    border: "none",
    padding: "12px 20px",
    borderRadius: "8px",
    cursor: "pointer",
    transition: "background 0.3s ease",
  },
};

export default BookAd;
