import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { getNewspapers, getUserData } from "../services/api";
import { FiChevronDown, FiArrowRight, FiCheckCircle } from "react-icons/fi";


const AdBooking = () => {
  const [newspapers, setNewspapers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [authChecking, setAuthChecking] = useState(true);
  const [authError, setAuthError] = useState(false);
  const navigate = useNavigate();

  // Check if the user is logged in
  useEffect(() => {
    const checkUser = async () => {
      try {
        setAuthChecking(true);
        const token = localStorage.getItem("token");

        if (!token) {
          setAuthError(true);
          setAuthChecking(false);
          return;
        }

        const userData = await getUserData();
        if (userData) {
          setUser(userData);
          setAuthError(false);
          fetchNewspapers();
        } else {
          localStorage.removeItem("token");
          setAuthError(true);
        }
      } catch (error) {
        console.error("Error checking user:", error);
        setAuthError(true);
      } finally {
        setAuthChecking(false);
      }
    };

    checkUser();
  }, []);

  const fetchNewspapers = async () => {
    try {
      setLoading(true);
      const data = await getNewspapers();
      setNewspapers(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error("Error fetching newspapers:", error);
      setNewspapers([]);
    } finally {
      setLoading(false);
    }
  };

  const handleNewspaperChange = (e) => {
    const selectedId = parseInt(e.target.value);
    const selectedNewspaper = newspapers.find((paper) => paper.id === selectedId);

    if (selectedNewspaper) {
      navigate(`/newspaper-layout?name=${encodeURIComponent(selectedNewspaper.name)}`);
    }
  };

  const handleLoginRedirect = () => {
    navigate("/login");
  };

  if (authChecking) {
    return (
      <div className="book-ad-container">
        <div className="loading-state">
          <div className="loading-spinner"></div>
          <p className="loading-text">Verifying your account...</p>
        </div>
      </div>
    );
  }

  if (authError) {
    return (
      <div className="book-ad-container">
        <div className="error-state">
          <h2>Authentication Issue</h2>
          <p>There was a problem verifying your login. Please try logging in again.</p>
          <button className="booking-button" onClick={handleLoginRedirect}>
            Go to Login <FiArrowRight />
          </button>
        </div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="book-ad-container">
        <div className="error-state">
          <h2>Authentication Required</h2>
          <p>You need to be logged in to book an ad.</p>
          <button className="booking-button" onClick={handleLoginRedirect}>
            Go to Login <FiArrowRight />
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="ad-booking-container">
      <div className="ad-booking-card">
        <div className="booking-header">
          <div className="header-icon">
            <FiCheckCircle />
          </div>
          <h1>Newspaper Ad Booking</h1>
          <p className="header-subtitle">Premium advertising solutions for businesses</p>
        </div>

        {authChecking ? (
          <div className="loading-state">
            <div className="loading-spinner"></div>
            <p>Verifying your session...</p>
          </div>
        ) : authError || !user ? (
          <div className="auth-error">
            <h2>Authentication Required</h2>
            <p>Please sign in to access our advertising platform</p>
            <button 
              className="primary-button"
              onClick={handleLoginRedirect}
            >
              Sign In <FiArrowRight className="button-icon" />
            </button>
          </div>
        ) : (
          <div className="booking-content">
            <div className="user-welcome">
              <p>Welcome back, <span>{user.name}</span></p>
              <p className="welcome-subtext">Ready to create your next advertisement?</p>
            </div>

            <div className="form-section">
              <div className="form-group">
                <label>Select Newspaper</label>
                <div className="custom-select">
                  <select
                    onChange={handleNewspaperChange}
                    disabled={loading || newspapers.length === 0}
                  >
                    <option value="">{loading ? "Loading options..." : "Select publication"}</option>
                    {newspapers.map((paper) => (
                      <option key={paper.id} value={paper.id}>
                        {paper.name} • ₹{paper.price_per_word.toLocaleString()}/word
                      </option>
                    ))}
                  </select>
                  <FiChevronDown className="select-icon" />
                </div>
              </div>
            </div>

            <div className="booking-features">
              <div className="feature">
                <div className="feature-icon">✓</div>
                <p>Instant booking confirmation</p>
              </div>
              <div className="feature">
                <div className="feature-icon">✓</div>
                <p>Competitive pricing</p>
              </div>
              <div className="feature">
                <div className="feature-icon">✓</div>
                <p>Nationwide distribution</p>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdBooking;
