import React, { useState } from "react";
import "../../styles/NewspaperLayout.css"; // Ensure CSS path is correct

const NewspaperLayout = () => {
  const [selectedAd, setSelectedAd] = useState(null);
  const [selectedPrice, setSelectedPrice] = useState(null);

  // Prices for different ad sizes
  const adPrices = {
    large: 5000,
    medium: 3000,
    small: 1500,
  };

  const handleAdClick = (size, index) => {
    setSelectedAd(index);
    setSelectedPrice(adPrices[size]);
  };

  return (
    <div className="newspaper-container">
      <div className="newspaper-content">
        {/* Newspaper Header */}
        <h1 className="newspaper-title">THE VIRTUAL TIMES</h1>
        <p className="sub-title">Your Trusted Daily Source</p>
        <div className="separator"></div> {/* Separation Line */}

        {/* Main Headline */}
        <div className="main-headline">
          <h2>Pakistanâ€™s FIA to Collaborate with CBI on Fighting Terror</h2>
          <p>Intense cross-border security discussions are underway to curb international crime and ensure stability...</p>
        </div>

        {/* First Ad Section */}
        <div className="ads-container">
          <div className="ad-space large" onClick={() => handleAdClick("large", 0)}>
            ðŸ”¹ **Exclusive Front Page Ad Space** ðŸ”¹  
            {selectedAd === 0 && (
              <div>
                <p className="ad-price">Price: â‚¹{selectedPrice}</p>
                <button className="book-ad-button">Book Ad</button>
              </div>
            )}
          </div>
        </div>

        {/* Multiple Fake News Articles */}
        {[
          { title: "Tech Giants Battle Over AI Supremacy", content: "Leading technology companies are competing fiercely..." },
          { title: "Scientists Discover Possible Signs of Life on Distant Planet", content: "Researchers at NASA have detected potential bio-signatures..." },
          { title: "Stock Markets Hit Record Highs Amid Economic Recovery", content: "The global stock markets have reached all-time highs..." },
          { title: "Climate Change Conference Concludes with New Global Agreements", content: "World leaders have signed a historic agreement..." },
          { title: "New Smartphone Release Breaks Sales Records", content: "The latest flagship smartphone has taken the market by storm..." },
          { title: "SpaceX Successfully Launches Next-Gen Satellite", content: "Elon Musk's SpaceX has successfully launched a new satellite..." },
          { title: "Breakthrough in Cancer Treatment Announced", content: "Scientists have developed a new cancer treatment with promising results..." },
          { title: "Revolutionary Electric Car Achieves 1,000 Km Range on Single Charge", content: "A new electric vehicle prototype has shattered records..." }
        ].map((article, index) => (
          <div key={index} className="news-article">
            <h2>{article.title}</h2>
            <p>{article.content}</p>
          </div>
        ))}

        {/* Ad Sections */}
        {[1, 2, 3, 4, 5, 6, 7, 8].map((index) => (
          <div className="ads-container" key={index}>
            <div className={`ad-space ${index % 2 === 0 ? "medium" : "small"}`} onClick={() => handleAdClick(index % 2 === 0 ? "medium" : "small", index)}>
              ðŸ”¹ **Ad Space {index + 1}** ðŸ”¹  
              {selectedAd === index && (
                <div>
                  <p className="ad-price">Price: â‚¹{selectedPrice}</p>
                  <button className="book-ad-button">Book Ad</button>
                </div>
              )}
            </div>
          </div>
        ))}

      </div>
    </div>
  );
};

export default NewspaperLayout;
