import axios from "axios";

const API_BASE_URL = "http://localhost:5000/api";

// ✅ Get token from localStorage - now exported
export const getToken = () => localStorage.getItem("token");

// ✅ Fetch newspapers
export const getNewspapers = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/newspapers`);
    return response.data;
  } catch (error) {
    console.error("❌ Fetch Newspapers Error:", error);
    return [];
  }
};

// ✅ Login API
export const loginUser = async (email, password) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/auth/login`, {
      email,
      password,
    });

    if (response.data.token) {
      localStorage.setItem("token", response.data.token);
      console.log("✅ Token saved:", response.data.token);
    } else {
      console.warn("⚠️ No token received from the server.");
    }

    return response.data; // Return response data
  } catch (error) {
    console.error("❌ Login Error:", error.response?.data || error.message);
    throw error; // Rethrow error for handling in components
  }
};

// ✅ Signup API
export const signupUser = async (userData) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/auth/signup`, userData);
    return response.data; // Return signup response
  } catch (error) {
    console.error("❌ Signup Error:", error.response?.data || error.message);
    throw error; // Rethrow error
  }
};

export const getUserData = async () => {
  const token = getToken();
  console.log("🔍 Token being sent:", token);

  if (!token) {
    console.warn("❌ No token found. User is not logged in.");
    return null;
  }

  try {
    const response = await axios.get(`${API_BASE_URL}/auth/user`, {
      headers: {
        Authorization: `Bearer ${token}`, // ✅ Include token
      },
    });

    console.log("✅ User Data Response:", response.data);
    // Check if response.data is the user object or contains a user property
    if (response.data && response.data.user) {
      return response.data.user;
    } else if (response.data) {
      return response.data; // Return the response data as the user
    } else {
      console.warn("⚠️ User data response is empty or invalid");
      return null;
    }
  } catch (error) {
    console.error("❌ User Fetch Error:", error.response?.data || error.message);
    
    // Don't automatically remove token or log the user out on error
    // Only do so if explicitly 401 Unauthorized
    if (error.response?.status === 401) {
      console.warn("🚨 Unauthorized: Invalid or expired token");
      // Keeping the token for debugging purposes for now
      // localStorage.removeItem("token");
    }
    
    return null; // Return null if unauthorized
  }
};
// ✅ Decode JWT to Get User Info (Alternative)
export const getAuthenticatedUser = () => {
  const token = getToken();
  if (!token) return null;

  try {
    // Decode JWT payload (middle part)
    const payload = JSON.parse(atob(token.split(".")[1]));
    console.log("🔍 Decoded Token Payload:", payload);
    return payload;
  } catch (error) {
    console.error("❌ Token decoding error:", error);
    return null;
  }
};

// ✅ Logout User
export const logoutUser = () => {
  localStorage.removeItem("token");
  console.log("🚪 User logged out successfully.");
};
