import React, { useState, useEffect, useCallback, useContext } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "../../styles/DeccanChroniclesBooking.css";
import { AuthContext } from "../../context/AuthContext";

const DeccanChroniclesBooking = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { user, loading } = useContext(AuthContext);

  const { price: basePrice, newspaperId = 1, spaceId, newspaperName } = location.state || {};

  const [adTitle, setAdTitle] = useState("");
  const [adContent, setAdContent] = useState("");
  const [category, setCategory] = useState("Jobs");
  const [duration, setDuration] = useState(1);
  const [totalPrice, setTotalPrice] = useState(basePrice || 0);

  const calculatePrice = useCallback((content, duration) => {
    const contentLength = content.length;
    const pricePerCharacter = 0.5;
    const contentPrice = basePrice + contentLength * pricePerCharacter;
    return contentPrice * duration;
  }, [basePrice]);

  useEffect(() => {
    setTotalPrice(calculatePrice(adContent, duration));
  }, [adContent, duration, calculatePrice]);

  const handlePayment = async (bookingDetails) => {
    try {
      const response = await fetch("http://localhost:5000/api/payment/create-order", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${user.token}`,
        },
        body: JSON.stringify({ amount: bookingDetails.price }),
      });

      if (!response.ok) {
        throw new Error("Failed to initiate payment");
      }

      const { id: orderId, currency, amount } = await response.json();

      const options = {
        key: "YOUR_RAZORPAY_KEY_ID", // Replace with your Razorpay Key ID
        amount,
        currency,
        name: "Deccan Chronicles",
        description: "Ad Booking Payment",
        order_id: orderId,
        handler: async function (response) {
          alert("Payment Successful!");
          await finalizeBooking(bookingDetails, response);
        },
        prefill: {
          name: user.name,
          email: user.email,
        },
        theme: {
          color: "#3399cc",
        },
      };

      const rzp = new window.Razorpay(options);
      rzp.open();
    } catch (error) {
      console.error("Payment Error:", error);
      alert("Error while processing payment. Try again.");
    }
  };
 
  const finalizeBooking = async (bookingDetails, paymentResponse) => {
    try {
      const response = await fetch("http://localhost:5000/api/ad-booking/book", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${user.token}`,
        },
        body: JSON.stringify({
          ...bookingDetails,
          paymentId: paymentResponse.razorpay_payment_id,
        }),
      });

      if (response.ok) {
        alert("ðŸŽ‰ Ad booked successfully and payment confirmed!");
        navigate("/");
      } else {
        const errorData = await response.json();
        alert(`âŒ Booking failed: ${errorData.message || "Try again."}`);
      }
    } catch (error) {
      console.error("Error finalizing booking:", error);
      alert("Error occurred during final booking.");
    }
  };

  const handleBooking = async () => {
    if (!adTitle || !adContent) {
      alert("Please fill in all fields.");
      return;
    }

    if (!user) {
      alert("Please log in to book an ad.");
      navigate("/login");
      return;
    }

    const bookingDetails = {
      userId: user.id,
      newspaperId,
      spaceId,
      title: adTitle,
      content: adContent,
      category,
      duration,
      price: totalPrice,
      newspaperName,
      email: user.email,
    };

    handlePayment(bookingDetails);
  };

  if (loading) return <p>Loading user data...</p>;

  return (
    <div className="deccan-booking">
      <div className="booking-card">
        <h1 className="newspaper-title">{newspaperName || "Deccan Chronicles"}</h1>
        <p className="subtitle">Your Trusted Daily Newspaper</p>

        <h2 className="section-title">Classified Ads Booking</h2>

        <div className="form-group">
          <label>Ad Title:</label>
          <input
            type="text"
            value={adTitle}
            onChange={(e) => setAdTitle(e.target.value)}
            placeholder="Enter your ad title"
            required
          />
        </div>

        <div className="form-group">
          <label>Ad Content:</label>
          <textarea
            value={adContent}
            onChange={(e) => setAdContent(e.target.value)}
            placeholder="Write your ad content here..."
            required
          />
        </div>

        <div className="form-group">
          <label>Category:</label>
          <select value={category} onChange={(e) => setCategory(e.target.value)}>
            <option value="Jobs">Jobs</option>
            <option value="Real Estate">Real Estate</option>
            <option value="Services">Services</option>
            <option value="Announcements">Announcements</option>
          </select>
        </div>

        <div className="form-group">
          <label>Duration (in days):</label>
          <input
            type="number"
            value={duration}
            onChange={(e) => setDuration(Math.min(10, Math.max(1, e.target.value)))}
            min="1"
            max="10"
            required
          />
        </div>

        <p><strong>Total Price:</strong> â‚¹{totalPrice}</p>

        <button className="book-ad-button" onClick={handleBooking}>
          Book Ad & Pay
        </button>
      </div>
    </div>
  );
};

export default DeccanChroniclesBooking;
