import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { FiUser, FiMail, FiPhone, FiLock, FiArrowRight } from "react-icons/fi";

const Signup = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    password: "",
  });

  const [errors, setErrors] = useState({
    phone: "",
    email: "",
    password: "",
    general: ""
  });
  
  const [isSubmitting, setIsSubmitting] = useState(false);
  const navigate = useNavigate();

  const validateEmail = (email) => {
    const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return re.test(email);
  };

  const validatePassword = (password) => {
    return password.length >= 8;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    let error = "";

    if (name === "phone") {
      if (!/^\d*$/.test(value)) {
        error = "Phone number must contain only digits.";
      } else if (value.length > 10) {
        error = "Phone number cannot exceed 10 digits.";
      }
    } else if (name === "email" && value && !validateEmail(value)) {
      error = "Please enter a valid email address.";
    } else if (name === "password" && value && !validatePassword(value)) {
      error = "Password must be at least 8 characters long.";
    }

    setErrors({ ...errors, [name]: error });
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    // Validate all fields
    const newErrors = { ...errors };
    let isValid = true;

    if (formData.phone.length !== 10) {
      newErrors.phone = "Phone number must be exactly 10 digits.";
      isValid = false;
    }

    if (!validateEmail(formData.email)) {
      newErrors.email = "Please enter a valid email address.";
      isValid = false;
    }

    if (!validatePassword(formData.password)) {
      newErrors.password = "Password must be at least 8 characters long.";
      isValid = false;
    }

    setErrors(newErrors);

    if (!isValid) {
      setIsSubmitting(false);
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/api/auth/signup", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || "Signup failed");
      }

      // On successful signup
      localStorage.setItem("token", data.token);
      navigate("/dashboard");
    } catch (error) {
      setErrors({ ...errors, general: error.message });
    } finally {
      setIsSubmitting(false);
    }
  };

  // Inline styles
  const styles = {
    container: {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      minHeight: "100vh",
      backgroundColor: "#f5f7fa",
      padding: "2rem",
      backgroundImage: "linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)",
    },
    card: {
      width: "100%",
      maxWidth: "450px",
      background: "white",
      borderRadius: "16px",
      boxShadow: "0 10px 30px rgba(0, 0, 0, 0.08)",
      padding: "2.5rem",
      animation: "fadeIn 0.5s ease",
    },
    header: {
      textAlign: "center",
      marginBottom: "2rem",
    },
    headerTitle: {
      color: "#2b2d42",
      fontSize: "1.8rem",
      fontWeight: "700",
      marginBottom: "0.5rem",
    },
    headerSubtitle: {
      color: "#495057",
      fontSize: "0.95rem",
    },
    form: {
      display: "flex",
      flexDirection: "column",
      gap: "1.25rem",
    },
    formGroup: {
      display: "flex",
      flexDirection: "column",
      gap: "0.5rem",
    },
    inputContainer: {
      position: "relative",
      display: "flex",
      alignItems: "center",
    },
    inputIcon: {
      position: "absolute",
      left: "1rem",
      color: "#adb5bd",
      fontSize: "1.1rem",
    },
    input: {
      width: "100%",
      padding: "0.9rem 1rem 0.9rem 2.5rem",
      border: "1px solid #e9ecef",
      borderRadius: "8px",
      fontSize: "0.95rem",
      color: "#2b2d42",
      transition: "all 0.2s ease",
    },
    inputFocus: {
      outline: "none",
      borderColor: "#4361ee",
      boxShadow: "0 0 0 3px rgba(67, 97, 238, 0.15)",
    },
    errorText: {
      color: "#ef233c",
      fontSize: "0.8rem",
      marginTop: "-0.25rem",
    },
    errorMessage: {
      backgroundColor: "rgba(239, 35, 60, 0.1)",
      color: "#ef233c",
      padding: "0.75rem 1rem",
      borderRadius: "8px",
      fontSize: "0.9rem",
      marginBottom: "1rem",
      textAlign: "center",
    },
    button: {
      backgroundColor: "#4361ee",
      color: "white",
      border: "none",
      borderRadius: "8px",
      padding: "1rem",
      fontSize: "1rem",
      fontWeight: "500",
      cursor: "pointer",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      gap: "0.5rem",
      transition: "all 0.2s ease",
      marginTop: "0.5rem",
    },
    buttonHover: {
      backgroundColor: "#3a56d4",
      transform: "translateY(-1px)",
    },
    buttonDisabled: {
      backgroundColor: "#adb5bd",
      cursor: "not-allowed",
      opacity: "0.8",
    },
    buttonIcon: {
      fontSize: "1.1rem",
    },
    spinner: {
      width: "1.25rem",
      height: "1.25rem",
      border: "2px solid rgba(255, 255, 255, 0.3)",
      borderRadius: "50%",
      borderTopColor: "white",
      animation: "spin 1s ease-in-out infinite",
    },
    loginRedirect: {
      textAlign: "center",
      marginTop: "1.5rem",
      fontSize: "0.9rem",
      color: "#495057",
    },
    loginLink: {
      color: "#4361ee",
      fontWeight: "500",
      cursor: "pointer",
      transition: "color 0.2s ease",
    },
    loginLinkHover: {
      color: "#3a56d4",
      textDecoration: "underline",
    },
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <div style={styles.header}>
          <h1 style={styles.headerTitle}>Create Your Account</h1>
          <p style={styles.headerSubtitle}>Join us today and start your journey</p>
        </div>

        {errors.general && (
          <div style={styles.errorMessage}>
            {errors.general}
          </div>
        )}

        <form onSubmit={handleSubmit} style={styles.form}>
          <div style={styles.formGroup}>
            <div style={styles.inputContainer}>
              <FiUser style={styles.inputIcon} />
              <input
                type="text"
                name="name"
                placeholder="Full Name"
                value={formData.name}
                onChange={handleChange}
                required
                style={styles.input}
              />
            </div>
          </div>

          <div style={styles.formGroup}>
            <div style={styles.inputContainer}>
              <FiMail style={styles.inputIcon} />
              <input
                type="email"
                name="email"
                placeholder="Email Address"
                value={formData.email}
                onChange={handleChange}
                required
                style={{
                  ...styles.input,
                  ...(errors.email ? { borderColor: "#ef233c" } : {}),
                }}
                onFocus={(e) => {
                  e.target.style.outline = "none";
                  e.target.style.borderColor = "#4361ee";
                  e.target.style.boxShadow = "0 0 0 3px rgba(67, 97, 238, 0.15)";
                }}
                onBlur={(e) => {
                  e.target.style.borderColor = errors.email ? "#ef233c" : "#e9ecef";
                  e.target.style.boxShadow = "none";
                }}
              />
            </div>
            {errors.email && <span style={styles.errorText}>{errors.email}</span>}
          </div>

          <div style={styles.formGroup}>
            <div style={styles.inputContainer}>
              <FiPhone style={styles.inputIcon} />
              <input
                type="text"
                name="phone"
                placeholder="Phone Number"
                value={formData.phone}
                onChange={handleChange}
                required
                style={{
                  ...styles.input,
                  ...(errors.phone ? { borderColor: "#ef233c" } : {}),
                }}
                onFocus={(e) => {
                  e.target.style.outline = "none";
                  e.target.style.borderColor = "#4361ee";
                  e.target.style.boxShadow = "0 0 0 3px rgba(67, 97, 238, 0.15)";
                }}
                onBlur={(e) => {
                  e.target.style.borderColor = errors.phone ? "#ef233c" : "#e9ecef";
                  e.target.style.boxShadow = "none";
                }}
              />
            </div>
            {errors.phone && <span style={styles.errorText}>{errors.phone}</span>}
          </div>

          <div style={styles.formGroup}>
            <div style={styles.inputContainer}>
              <FiLock style={styles.inputIcon} />
              <input
                type="password"
                name="password"
                placeholder="Password (min 8 characters)"
                value={formData.password}
                onChange={handleChange}
                required
                style={{
                  ...styles.input,
                  ...(errors.password ? { borderColor: "#ef233c" } : {}),
                }}
                onFocus={(e) => {
                  e.target.style.outline = "none";
                  e.target.style.borderColor = "#4361ee";
                  e.target.style.boxShadow = "0 0 0 3px rgba(67, 97, 238, 0.15)";
                }}
                onBlur={(e) => {
                  e.target.style.borderColor = errors.password ? "#ef233c" : "#e9ecef";
                  e.target.style.boxShadow = "none";
                }}
              />
            </div>
            {errors.password && <span style={styles.errorText}>{errors.password}</span>}
          </div>

          <button
            type="submit"
            style={{
              ...styles.button,
              ...(isSubmitting ? styles.buttonDisabled : {}),
            }}
            disabled={isSubmitting}
            onMouseEnter={(e) => {
              if (!isSubmitting) {
                e.target.style.backgroundColor = "#3a56d4";
                e.target.style.transform = "translateY(-1px)";
              }
            }}
            onMouseLeave={(e) => {
              if (!isSubmitting) {
                e.target.style.backgroundColor = "#4361ee";
                e.target.style.transform = "translateY(0)";
              }
            }}
          >
            {isSubmitting ? (
              <span style={styles.spinner}></span>
            ) : (
              <>
                Sign Up <FiArrowRight style={styles.buttonIcon} />
              </>
            )}
          </button>
        </form>

        <div style={styles.loginRedirect}>
          Already have an account?{" "}
          <span
            style={styles.loginLink}
            onClick={() => navigate("/login")}
            onMouseEnter={(e) => {
              e.target.style.color = "#3a56d4";
              e.target.style.textDecoration = "underline";
            }}
            onMouseLeave={(e) => {
              e.target.style.color = "#4361ee";
              e.target.style.textDecoration = "none";
            }}
          >
            Log in
          </span>
        </div>
      </div>

      {/* Global styles for animations */}
      <style>
        {`
          @keyframes fadeIn {
            from {
              opacity: 0;
              transform: translateY(10px);
            }
            to {
              opacity: 1;
              transform: translateY(0);
            }
          }
          @keyframes spin {
            to {
              transform: rotate(360deg);
            }
          }
        `}
      </style>
    </div>
  );
};

export default Signup;
