import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { getNewspapers, getUserData } from "../services/api";
import api from "../services/api";

const AdBooking = () => {
  const [newspapers, setNewspapers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [authChecking, setAuthChecking] = useState(true);
  const [authError, setAuthError] = useState(false);
  const navigate = useNavigate();

  // Check if the user is logged in
  useEffect(() => {
    const checkUser = async () => {
      try {
        setAuthChecking(true);
        const token = localStorage.getItem("token");

        if (!token) {
          setAuthError(true);
          setAuthChecking(false);
          return;
        }

        const userData = await getUserData();
        if (userData) {
          setUser(userData);
          setAuthError(false);
          fetchNewspapers();
        } else {
          localStorage.removeItem("token");
          setAuthError(true);
        }
      } catch (error) {
        console.error("Error checking user:", error);
        setAuthError(true);
      } finally {
        setAuthChecking(false);
      }
    };

    checkUser();
  }, []);

  const fetchNewspapers = async () => {
    try {
      setLoading(true);
      const data = await getNewspapers();
      setNewspapers(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error("Error fetching newspapers:", error);
      setNewspapers([]);
    } finally {
      setLoading(false);
    }
  };

  const handleNewspaperChange = (e) => {
    const selectedId = parseInt(e.target.value);
    const selectedNewspaper = newspapers.find((paper) => paper.id === selectedId);

    if (selectedNewspaper) {
      navigate(`/newspaper-layout?name=${encodeURIComponent(selectedNewspaper.name)}`);
    }
  };

  const handleLoginRedirect = () => {
    navigate("/login");
  };

  if (authChecking) {
    return (
      <div className="book-ad-wrapper">
        <div className="loading-text">
          <div className="spinner"></div>
          Verifying your account...
        </div>
      </div>
    );
  }

  if (authError) {
    return (
      <div className="book-ad-error">
        <h2>Authentication Issue</h2>
        <p>There was a problem verifying your login. Please try logging in again.</p>
        <button className="button" onClick={handleLoginRedirect}>
          Go to Login
        </button>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="book-ad-error">
        <h2>Authentication Required</h2>
        <p>You need to be logged in to book an ad.</p>
        <button className="button" onClick={handleLoginRedirect}>
          Go to Login
        </button>
      </div>
    );
  }

  return (
    <div className="ad-booking-page">
      {authChecking ? (
        <div className="auth-loading">
          <div className="spinner"></div>
          <p>Verifying your account...</p>
        </div>
      ) : authError || !user ? (
        <div className="auth-error-card">
          <h2>Authentication Required</h2>
          <p>Please log in to book advertisements</p>
          <button className="auth-button" onClick={handleLoginRedirect}>
            Go to Login <FiArrowRight />
          </button>
        </div>
      ) : (
        <div className="booking-card">
          <div className="booking-header">
            <h2>Book Your Ad</h2>
            <p className="welcome-text">Welcome, {user.name}</p>
          </div>
          
          <div className="booking-form">
            <div className="form-group">
              <label>Select Newspaper</label>
              <div className="select-wrapper">
                <select
                  className="form-select"
                  onChange={handleNewspaperChange}
                  disabled={loading || newspapers.length === 0}
                >
                  <option value="">{loading ? "Loading newspapers..." : "Select a newspaper"}</option>
                  {newspapers.map((paper) => (
                    <option key={paper.id} value={paper.id}>
                      {paper.name} - ₹{paper.price_per_word}/word
                    </option>
                  ))}
                </select>
                <FiChevronDown className="select-icon" />
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};
export default AdBooking;
