import React, { useState } from "react";
import "../../styles/NewspaperLayout.css"; // Ensure this path is correct

const NewspaperLayout = () => {
  const [selectedBackground, setSelectedBackground] = useState("default");

  // Background options
  const backgrounds = {
    default: "https://m.media-amazon.com/images/I/61oWAUKcCrL._AC_UF894,1000_QL80_.jpg",
    classic: "https://www.wallpaperflare.com/static/885/687/780/paper-texture-textured-background-wallpaper.jpg",
    modern: "https://static.vecteezy.com/system/resources/previews/002/113/841/non_2x/abstract-newspaper-background-free-vector.jpg",
  };

  return (
    <div
      className="newspaper-container"
      style={{ backgroundImage: `url(${backgrounds[selectedBackground]})` }}
    >
      <div className="newspaper-content">
        <h1 className="newspaper-title">THE VIRTUAL TIMES</h1>
        <p className="sub-title">Your Daily Source of News</p>

        {/* Background Selector */}
        <div className="background-selector">
          <label>Choose Background: </label>
          <select onChange={(e) => setSelectedBackground(e.target.value)}>
            <option value="default">Default</option>
            <option value="classic">Classic</option>
            <option value="modern">Modern</option>
          </select>
        </div>

        {/* News Headline Section */}
        <div className="news-headline">
          <h2>Pakistanâ€™s FIA to Collaborate with CBI on Fighting Terror</h2>
          <p>Outcomes about decisive war on crime and global track collaborations...</p>
        </div>

        {/* Advertisement Spaces */}
        <div className="ads-container">
          <div className="ad-space large">Large Advertisement Space (Click to Book)</div>
          <div className="ad-row">
            <div className="ad-space medium">Medium Advertisement Space (Click to Book)</div>
            <div className="ad-space small">Small Advertisement Space (Click to Book)</div>
          </div>
          <div className="ad-row">
            <div className="ad-space small">Small Advertisement Space (Click to Book)</div>
            <div className="ad-space medium">Medium Advertisement Space (Click to Book)</div>
          </div>
          <div className="ad-space large">Large Advertisement Space (Click to Book)</div>
          <div className="ad-row">
            <div className="ad-space medium">Medium Advertisement Space (Click to Book)</div>
            <div className="ad-space large">Large Advertisement Space (Click to Book)</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NewspaperLayout;
