import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/AdminDashboard.css';

const AdminDashboard = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [token, setToken] = useState('');
  const [pendingAds, setPendingAds] = useState([]);

  // Handle admin login
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/admin/login', {
        username,
        password,
      });
      setToken(response.data.token);
      setIsLoggedIn(true);
      fetchPendingAds();
    } catch (error) {
      alert('Invalid credentials');
    }
  };
  
  const fetchPendingAds = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/admin/pending-ads', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setPendingAds(response.data);
    } catch (error) {
      console.error('Error fetching pending ads:', error);
    }
  };
  
  const updateAdStatus = async (adId, status) => {
    try {
      await axios.put(
        'http://localhost:5000/api/admin/update-status',
        { adId, status },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert(`Ad marked as ${status}`);
      fetchPendingAds();
    } catch (error) {
      console.error('Error updating ad status:', error);
    }
  };
  

  if (!isLoggedIn) {
    return (
      <div className="admin-login">
        <h2>Admin Login</h2>
        <form onSubmit={handleLogin}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit">Login</button>
        </form>
      </div>
    );
  }

  return (
    <div className="admin-dashboard">
      <h2>Pending Ad Approvals</h2>
      {pendingAds.length === 0 ? (
        <p>No pending ads</p>
      ) : (
        <ul>
          {pendingAds.map((ad) => (
            <li key={ad.id}>
              <p>Ad ID: {ad.id}</p>
              <p>Content: {ad.content}</p>
              <p>Price: {ad.price}</p>
              <button onClick={() => updateAdStatus(ad.id, 'booked')}>Approve</button>
              <button onClick={() => updateAdStatus(ad.id, 'rejected')}>Reject</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default AdminDashboard;
