import { useState, useEffect, useContext } from "react";
import axios from "axios";
import AuthContext from "../context/AuthContext"; // Ensure this import exists

const AdBooking = () => {
  const { user } = useContext(AuthContext); // Get logged-in user details
  const [newspapers, setNewspapers] = useState([]);
  const [selectedNewspaper, setSelectedNewspaper] = useState(null);
  const [adSpaces, setAdSpaces] = useState([]);
  const [selectedAdSpace, setSelectedAdSpace] = useState(null);
  const [content, setContent] = useState("");
  const [price, setPrice] = useState(0);
  const [message, setMessage] = useState("");

  useEffect(() => {
    // Fetch all newspapers
    axios
      .get("http://localhost:5000/api/newspapers")
      .then((response) => setNewspapers(response.data))
      .catch((error) => console.error("Error fetching newspapers:", error));
  }, []);

  c
  const handleAdSpaceSelect = (space) => {
    setSelectedAdSpace(space);
    setPrice(space.price); // Set initial price based on selected space
  };

  const handleContentChange = (e) => {
    setContent(e.target.value);
    const newPrice = selectedAdSpace ? selectedAdSpace.price + e.target.value.length * 0.5 : 0;
    setPrice(newPrice); // Adjust price dynamically
  };

  const handleBooking = async () => {
    if (!user) {
      setMessage("Please log in to book an ad.");
      return;
    }
    if (!selectedNewspaper || !selectedAdSpace || !content.trim()) {
      setMessage("Please fill all fields before booking.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/book-ad", {
        userId: user.id,
        newspaperId: selectedNewspaper,
        spaceId: selectedAdSpace.id,
        content,
        price,
        email: user.email, // Use authenticated user's email
      });

      setMessage(response.data.message);
      setContent(""); // Reset content field after booking
      setSelectedAdSpace(null); // Reset selection after booking
      setPrice(0);

      // Refresh available ad spaces (removes booked ones)
      handleNewspaperSelect(selectedNewspaper);
    } catch (error) {
      console.error("Error booking ad:", error);
      setMessage("Error booking ad. Please try again.");
    }
  };

  return (
    <div>
      <h2>Book an Ad</h2>

      <label>Select Newspaper:</label>
      <select value={selectedNewspaper || ""} onChange={(e) => handleNewspaperSelect(e.target.value)}>
        <option value="">-- Select --</option>
        {newspapers.map((n) => (
          <option key={n.id} value={n.id}>
            {n.name}
          </option>
        ))}
      </select>

      {selectedNewspaper && (
        <>
          <h3>Available Ad Spaces for {newspapers.find((n) => n.id === selectedNewspaper)?.name}:</h3>

          {adSpaces.length === 0 ? (
            <p>No ad spaces available for this newspaper.</p>
          ) : (
            <ul>
              {adSpaces.map((space) => (
                <li key={space.id} onClick={() => handleAdSpaceSelect(space)} style={{ cursor: "pointer" }}>
                  {space.space_name} - ${space.price}
                </li>
              ))}
            </ul>
          )}
        </>
      )}

      {selectedAdSpace && (
        <div>
          <h3>Enter Ad Content:</h3>
          <textarea value={content} onChange={handleContentChange} rows="4" cols="50"></textarea>

          <h4>Total Price: ${price.toFixed(2)}</h4>

          <button onClick={handleBooking}>Book Ad</button>
        </div>
      )}

      {message && <p>{message}</p>}
    </div>
  );
};

export default AdBooking;
