import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { getUserData } from "../services/api";

const NewspaperLayout = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const newspaperName = queryParams.get("name");

  // Check authentication first
  useEffect(() => {
    const checkAuth = async () => {
      try {
        setLoading(true);
        const userData = await getUserData();
        console.log("User data in Newspaper Layout:", userData);
        
        if (userData) {
          setUser(userData);
        } else {
          // Instead of a popup, we'll redirect to login
          navigate("/login", { 
            state: { 
              from: location.pathname + location.search,
              message: "Please log in to book an ad." 
            } 
          });
        }
      } catch (error) {
        console.error("Authentication error:", error);
      } finally {
        setLoading(false);
      }
    };
    
    checkAuth();
  }, [navigate, location]);

  // Return the layout content once authenticated
  return (
    <div className="newspaper-layout">
      {loading ? (
        <div>Loading...</div>
      ) : (
        <>
          <div className="header">
            <h1>Your Trusted Daily Newspaper</h1>
          </div>
          
          <div className="content">
            <h2>Classified Ads Booking</h2>
            
            {/* Ad form fields */}
            <div className="form-group">
              <label>Ad Title:</label>
              <input 
                type="text" 
                className="form-control"
                placeholder="Enter your ad title"
              />
            </div>
            
            <div className="form-group">
              <label>Ad Content:</label>
              <textarea 
                className="form-control"
                rows="4"
                placeholder="Enter your ad content"
              ></textarea>
            </div>
            
            <div className="form-group">
              <label>Category:</label>
              <select className="form-control">
                <option value="">Select a category</option>
                <option value="jobs">Jobs</option>
                <option value="real-estate">Real Estate</option>
                <option value="services">Services</option>
                <option value="for-sale">For Sale</option>
              </select>
            </div>
            
            <div className="form-group">
              <label>Project Duration (in days):</label>
              <input 
                type="number" 
                className="form-control"
                min="1"
              />
            </div>
            
            <div className="form-group">
              <label>Total Price: ₹3096.5</label>
            </div>
            
            <button className="btn btn-primary">Book Ad</button>
          </div>
        </>
      )}
    </div>
  );
};

export default NewspaperLayout;
