import React, { useState } from 'react';

const PaymentPage = () => {
  const [cardNumber, setCardNumber] = useState('');
  const [expiryDate, setExpiryDate] = useState('');
  const [cvv, setCvv] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  const handlePayment = async (event) => {
    event.preventDefault(); // Prevent default form submission

    setLoading(true);
    setError(null);
    setSuccess(false);

    const paymentData = {
      cardNumber,
      expiryDate,
      cvv,
    };

    try {
      // Replace with your actual payment API call
      const response = await fakePaymentApi(paymentData);
      
      if (response.success) {
        setSuccess(true);
        console.log("Payment successful:", response);
      } else {
        setError(response.message || 'Payment failed.');
      }
    } catch (err) {
      console.error("Error processing payment:", err);
      setError('An error occurred during payment processing. Please try again later.');
    } finally {
      setLoading(false); // Stop loading regardless of the outcome
    }
  };

  const fakePaymentApi = async (data) => {
    // Simulate a network request. Replace this with your actual API call.
    return new Promise((resolve, reject) => {
      setTimeout(() => {
        if (Math.random() > 0.5) {  // Randomly resolve or reject to simulate success/failure
          resolve({ success: true });
        } else {
          reject({ success: false, message: 'Your payment was declined.' });
        }
      }, 1000);
    });
  };

  return (
    <div>
      <h1>Payment Page</h1>
      <form onSubmit={handlePayment}>
        <div>
          <label>
            Card Number:
            <input
              type="text"
              value={cardNumber}
              onChange={(e) => setCardNumber(e.target.value)}
              required
            />
          </label>
        </div>
        <div>
          <label>
            Expiry Date (MM/YYYY):
            <input
              type="text"
              value={expiryDate}
              onChange={(e) => setExpiryDate(e.target.value)}
              required
            />
          </label>
        </div>
        <div>
          <label>
            CVV:
            <input
              type="text"
              value={cvv}
              onChange={(e) => setCvv(e.target.value)}
              required
            />
          </label>
        </div>
        {loading ? (
          <p>Loading...</p>
        ) : (
          <button type="submit">Pay Now</button>
        )}
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {success && <p style={{ color: 'green' }}>Payment was successful!</p>}
      </form>
    </div>
  );
};

export default PaymentPage;
