import React, { useState } from "react";
import "../../styles/NewspaperLayout.css";
const backgrounds = {
  default: "",
  light: "url('../assets/light-bg.jpg')",
  dark: "url('../assets/dark-bg.jpg')",
  classic: "url('../assets/classic-bg.jpg')"
};

const NewspaperLayout = () => {
  const [currentPage, setCurrentPage] = useState(1);
  const [bgStyle, setBgStyle] = useState(backgrounds.default);

  const handleBackgroundChange = (event) => {
    setBgStyle(backgrounds[event.target.value]);
  };

  return (
    <div className="newspaper-container" style={{ backgroundImage: bgStyle }}>
      <h1 className="newspaper-title">The Times - Advertisement Booking</h1>

      {/* Background Selection */}
      <div className="background-selector">
        <label>Choose Background:</label>
        <select onChange={handleBackgroundChange}>
          <option value="default">Default</option>
          <option value="light">Light</option>
          <option value="dark">Dark</option>
          <option value="classic">Classic</option>
        </select>
      </div>

      {/* Page Navigation */}
      <div className="page-navigation">
        {[1, 2, 3].map((page) => (
          <button
            key={page}
            className={currentPage === page ? "active" : ""}
            onClick={() => setCurrentPage(page)}
          >
            Page {page}
          </button>
        ))}
      </div>

      {/* Content */}
      <div className="newspaper-content">
        <div className="news-section">
          {currentPage === 1 && <h2>Best Travel Destinations for 2025</h2>}
          {currentPage === 2 && <h2>Tech Innovations of the Year</h2>}
          {currentPage === 3 && <h2>Health & Wellness Trends</h2>}
        </div>

        <div className="ad-section">
          <button className="ad-box">Business Ad</button>
          <button className="ad-box">Matrimony Ad</button>
          <button className="ad-box">Travel Ad</button>
        </div>
      </div>
    </div>
  );
};

export default NewspaperLayout;
