import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../../styles/NewspaperLayout.css";

const NewspaperLayout = () => {
  const [selectedAd, setSelectedAd] = useState(null);
  const [selectedPrice, setSelectedPrice] = useState(null);
  const [selectedSize, setSelectedSize] = useState("");
  const [adSpaces, setAdSpaces] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const adPrices = {
    large: 5000,
    medium: 3000,
    small: 1500,
  };

  const adSpacePositions = [
    { id: 1, name: "Exclusive Front Page Ad Space", size: "large" },
    { id: 2, name: "Premium Half-Page Ad", size: "medium" },
    { id: 3, name: "Quarter-Page Ad", size: "small" },
    { id: 4, name: "Prime Full-Width Ad Spot", size: "large" }
  ];

  useEffect(() => {
    const fetchAdSpaces = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/ad-booking/ad-spaces/1");
        console.log("Fetched ad spaces:", response.data); // Debug log
        setAdSpaces(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching ad spaces:", error);
        setLoading(false);
      }
    };
    fetchAdSpaces();
  }, []);

  const getAdStatus = (positionName) => {
    const space = adSpaces.find(ad => ad.space_name === positionName);
    return space ? space.status : "available";
  };

  const handleAdClick = (position) => {
    const space = adSpaces.find(ad => ad.space_name === position.name);
    if (!space || space.status === "booked") return;
    
    setSelectedAd(space.id);
    setSelectedPrice(adPrices[position.size]);
    setSelectedSize(position.size);
  };

  const handleAdBooking = () => {
    if (!selectedAd) return;
    navigate("/deccan-chronicles-booking", {
      state: {
        size: selectedSize,
        price: selectedPrice,
        newspaperId: 1,
        newspaperName: "The Virtual Times",
        spaceId: selectedAd,
      },
    });
  };

  if (loading) return <div className="loading">Loading newspaper layout...</div>;

  return (
    <div className="newspaper-layout">
      <div className="newspaper-container">
        <h1 className="newspaper-title">THE VIRTUAL TIMES</h1>
        <p className="sub-title">Your Trusted Daily Source</p>
        <div className="separator"></div>

        <div className="main-headline">
          <h2>Pakistan's FIA to Collaborate with CBI on Fighting Terror</h2>
          <p>
            Intense cross-border security discussions are underway to curb
            international crime and ensure stability...
          </p>
        </div>

        {/* Ad Sections */}
        <div className="ad-sections-container">
          {/* First Ad - Large */}
          <div className="ad-section">
            <div
              className={`ad-space large ${getAdStatus(adSpacePositions[0].name) === "booked" ? "booked" : ""}`}
            >
              <div className="ad-content">
                <span className="ad-icon">ðŸ”¹</span>
                <strong>{adSpacePositions[0].name}</strong>
                <span className="ad-icon">ðŸ”¹</span>
              </div>
              {getAdStatus(adSpacePositions[0].name) === "booked" && (
                <div className="booked-label">Booked</div>
              )}
            </div>
          </div>

          {/* Second Ad Section - Medium and Small */}
          <div className="ad-section horizontal">
            <div
              className={`ad-space medium ${getAdStatus(adSpacePositions[1].name) === "booked" ? "booked" : ""}`}
              onClick={() => handleAdClick(adSpacePositions[1])}
            >
              <div className="ad-content">
                <span className="ad-icon">ðŸ”¹</span>
                <strong>{adSpacePositions[1].name}</strong>
                <span className="ad-icon">ðŸ”¹</span>
              </div>
              {getAdStatus(adSpacePositions[1].name) === "booked" ? (
                <div className="booked-label">Booked</div>
              ) : selectedAd === adSpaces.find(ad => ad.space_name === adSpacePositions[1].name)?.id && (
                <div className="booking-options">
                  <p className="ad-price">Price: â‚¹{selectedPrice}</p>
                  <button className="book-ad-button" onClick={handleAdBooking}>
                    Book Ad
                  </button>
                </div>
              )}
            </div>

            <div
              className={`ad-space small ${getAdStatus(adSpacePositions[2].name) === "booked" ? "booked" : ""}`}
            >
              <div className="ad-content">
                <span className="ad-icon">ðŸ”¹</span>
                <strong>{adSpacePositions[2].name}</strong>
                <span className="ad-icon">ðŸ”¹</span>
              </div>
              {getAdStatus(adSpacePositions[2].name) === "booked" && (
                <div className="booked-label">Booked</div>
              )}
            </div>
          </div>

          {/* Third Ad - Large */}
          <div className="ad-section">
            <div
              className={`ad-space large ${getAdStatus(adSpacePositions[3].name) === "booked" ? "booked" : ""}`}
            >
              <div className="ad-content">
                <span className="ad-icon">ðŸ”¹</span>
                <strong>{adSpacePositions[3].name}</strong>
                <span className="ad-icon">ðŸ”¹</span>
              </div>
              {getAdStatus(adSpacePositions[3].name) === "booked" && (
                <div className="booked-label">Booked</div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NewspaperLayout;
