import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import ".././PaymentPage.css"; // Optional: Add your styles

const PaymentPage = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const {
    price,
    newspaperName,
    adTitle,
    adContent,
    duration,
    email,
  } = location.state || {};

  const handlePayment = async () => {
    const options = {
      key: "YOUR_RAZORPAY_KEY_ID", // Replace with your Razorpay test key
      amount: price * 100, // Amount in paise (1 INR = 100 paise)
      currency: "INR",
      name: newspaperName || "Deccan Chronicles",
      description: `Ad Booking for ${adTitle}`,
      handler: function(response) {
        // Payment success handler
        alert(`Payment Successful! Payment ID: ${response.razorpay_payment_id}`);
        // You can redirect to a success page or update booking status in your backend here
        navigate("/payment-success");
      },
      prefill: {
        name: email.split("@")[0], // Use the email prefix as name
        email: email,
      },
      theme: {
        color: "#F37254", // Set your color code for the payment UI
      },
      modal: {
        ondismiss: function() {
          alert("Payment window closed");
        },
      },
    };

    const razorpay = new window.Razorpay(options);
    razorpay.open();
  };

  return (
    <div className="payment-page">
      <h1>Complete Your Payment</h1>
      <h2>{newspaperName || "Deccan Chronicles"}</h2>
      <p><strong>Ad Title:</strong> {adTitle}</p>
      <p><strong>Ad Content:</strong> {adContent}</p>
      <p><strong>Duration:</strong> {duration} day(s)</p>
      <p><strong>Total Price:</strong> â‚¹{price}</p>

      <button className="pay-button" onClick={handlePayment}>
        Pay Now
      </button>
    </div>
  );
};

export default PaymentPage;
