import axios from "axios";

// ‚úÖ Create Axios instance with base URL
const api = axios.create({
  baseURL: "http://localhost:5000/api",
});

// ‚úÖ Helper: Retrieve token from localStorage
export const getToken = () => localStorage.getItem("token");

// ‚úÖ Automatically add Authorization header if token exists
api.interceptors.request.use((config) => {
  const token = getToken();
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
}, (error) => Promise.reject(error));

// ‚úÖ Fetch user data (Authorization handled automatically)
export const getUserData = async () => {
  try {
    const response = await api.get("/auth/user");
    return response.data?.user || response.data; // Ensure user object is returned
  } catch (error) {
    console.error("‚ùå Error fetching user data:", error.response?.data || error.message);
    return null;
  }
};

// ‚úÖ Fetch available newspapers
export const getNewspapers = async () => {
  try {
    const response = await api.get("/newspapers");
    return response.data;
  } catch (error) {
    console.error("‚ùå Fetch Newspapers Error:", error);
    return [];
  }
};

// ‚úÖ Login User & Store Token
e

// ‚úÖ Signup User
export const signupUser = async (userData) => {
  try {
    const response = await api.post("/auth/signup", userData);
    return response.data;
  } catch (error) {
    console.error("‚ùå Signup Error:", error.response?.data || error.message);
    throw error;
  }
};

// ‚úÖ Book Ad - Requires Authorization
export const bookAd = async (adData) => {
  try {
    const response = await api.post("/ad-booking", adData);
    console.log("üì¢ Ad booked successfully:", response.data);
    return response.data;
  } catch (error) {
    console.error("‚ùå Error booking ad:", error.response?.data || error.message);
    throw error;
  }
};

// ‚úÖ Logout User
export const logoutUser = () => {
  localStorage.removeItem("token");
  console.log("üö™ User logged out successfully.");
};

// ‚úÖ Decode JWT to Get User Info
export const getAuthenticatedUser = () => {
  const token = getToken();
  if (!token) return null;
  try {
    const payload = JSON.parse(atob(token.split(".")[1]));
    console.log("üîç Decoded Token Payload:", payload);
    return payload;
  } catch (error) {
    console.error("‚ùå Token decoding error:", error);
    return null;
  }
};

export default api;
