import React, { useEffect, useState } from "react";
import { getNewspapers, bookAd } from "../service/api"; // Correct import
import { useNavigate } from "react-router-dom";

const BookAd = () => {
  const [newspapers, setNewspapers] = useState([]);
  const [selectedNewspaper, setSelectedNewspaper] = useState("");
  const [adContent, setAdContent] = useState("");
  const [price, setPrice] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchNewspapers = async () => {
      const data = await getNewspapers();
      setNewspapers(data); // Set available newspapers
    };

    fetchNewspapers();
  }, []);

  const handleNewspaperSelect = (newspaper) => {
    setSelectedNewspaper(newspaper);
    setPrice(newspaper.basePrice || 0); // Set base price if available
  };

  const handleAdContentChange = (e) => {
    setAdContent(e.target.value);
    setPrice(selectedNewspaper.basePrice + e.target.value.length * 2); // Example pricing logic
  };

  const handleBookAd = async () => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login"); // Redirect to login if not logged in
      return;
    }

    try {
      await bookAd({ newspaper: selectedNewspaper.name, content: adContent, price });
      alert("Ad booked successfully!");
    } catch (error) {
      alert("Failed to book ad. Please try again.");
    }
  };

  return (
    <div>
      <h2>Book an Advertisement</h2>
      <h3>Available Newspapers:</h3>
      {newspapers.length === 0 ? (
        <p>No newspapers available</p>
      ) : (
        <ul>
          {newspapers.map((paper) => (
            <li key={paper._id} onClick={() => handleNewspaperSelect(paper)}>
              {paper.name} - Base Price: ₹{paper.basePrice}
            </li>
          ))}
        </ul>
      )}

      {selectedNewspaper && (
        <div>
          <h3>Selected Newspaper: {selectedNewspaper.name}</h3>
          <textarea value={adContent} onChange={handleAdContentChange} placeholder="Enter Ad Content"></textarea>
          <p>Calculated Price: ₹{price}</p>
          <button onClick={handleBookAd}>Book Ad</button>
        </div>
      )}
    </div>
  );
};

export default BookAd;
