// ‚úÖ Get token from localStorage
const getToken = () => localStorage.getItem("token");

// ‚úÖ Fetch authenticated user
export const getUserData = async () => {
  const token = getToken();

  if (!token) {
    console.warn("‚ùå No token found in localStorage. User is not logged in.");
    return null;
  }

  try {
    const response = await axios.get(`${API_BASE_URL}/auth/user`, {
      headers: {
        Authorization: `Bearer ${token}`, // ‚úÖ Send token in the header
      },
    });
    console.log("‚úÖ User Data Response:", response.data);
    return response.data;
  } catch (error) {
    console.error("‚ùå User Fetch Error:", error.response?.data || error.message);

    if (error.response?.status === 401) {
      console.warn("üö® Invalid token. Logging out...");
      localStorage.removeItem("token"); // Clear invalid token
    }

    return null;
  }
};
