import React from "react";
import "../../styles/NewspaperLayout.css";

const NewspaperLayout = () => {
  const bookAd = (size) => {
    alert(`Booking a ${size} advertisement space.`);
  };

  return (
    <div className="newspaper-container">
      <h1>The Virtual Times</h1>
      <p className="subtitle">Your Trusted Source of Daily News</p>

      {/* Newspaper Grid Layout */}
      <div className="newspaper-grid">
        {/* Main News Section */}
        <div className="news">
          <h2>Pakistanâ€™s FIA to Collaborate with CBI on Fighting Terror</h2>
          <p>Outcomes about decisive war on crime and global track collaborations...</p>
        </div>

        <div className="news">
          <h2>Opposition Protests Against Fuel Price Hike</h2>
          <p>Massive protests on streets of West Bengal and Kerala...</p>
        </div>

        <div className="news">
          <h2>Stock Market Hits Record High</h2>
          <p>Investors celebrate as Sensex reaches all-time peak...</p>
        </div>

        <div className="news">
          <h2>Scientists Discover New Planet</h2>
          <p>NASA announces a planet similar to Earth in distant galaxy...</p>
        </div>

        {/* Ad Booking Spaces */}
        <div className="ad large-ad" onClick={() => bookAd("Large")}>
          Large Advertisement Space (Click to Book)
        </div>

        <div className="ad medium-ad" onClick={() => bookAd("Medium")}>
          Medium Advertisement Space (Click to Book)
        </div>

        <div className="ad small-ad" onClick={() => bookAd("Small")}>
          Small Advertisement Space (Click to Book)
        </div>

        <div className="ad banner-ad" onClick={() => bookAd("Banner")}>
          Banner Advertisement Space (Click to Book)
        </div>

        <div className="ad side-ad" onClick={() => bookAd("Side Ad")}>
          Side Advertisement Space (Click to Book)
        </div>
      </div>
    </div>
  );
};

export default NewspaperLayout;
