import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../../styles/NewspaperLayout.css";

const NewspaperLayout = ({ onSpaceSelect }) => {
  const [selectedAd, setSelectedAd] = useState(null);
  const [selectedPrice, setSelectedPrice] = useState(null);
  const [selectedSize, setSelectedSize] = useState("");
  const navigate = useNavigate();

  // Ad spaces with unique spaceIds
  const adSpaces = [
    { id: 101, size: "large", label: "Exclusive Front Page Ad Space", price: 5000 },
    { id: 102, size: "medium", label: "Premium Half-Page Ad", price: 3000 },
    { id: 103, size: "small", label: "Quarter-Page Ad", price: 1500 },
    { id: 104, size: "large", label: "Prime Full-Width Ad Spot", price: 5000 },
  ];

  // Handle ad selection
  const handleAdClick = (space) => {
    setSelectedAd(space.id);
    setSelectedPrice(space.price);
    setSelectedSize(space.size);

    // Pass spaceId to parent (BookAd.js)
    if (onSpaceSelect) {
      onSpaceSelect(space.id);
    }
  };

  // Navigate to the ad booking page with selected ad details
  const handleAdBooking = () => {
    if (!selectedAd) {
      alert("Please select an ad space!");
      return;
    }

    navigate("/deccan-chronicles-booking", {
      state: { size: selectedSize, price: selectedPrice, spaceId: selectedAd },
    });
  };

  return (
    <div className="newspaper-layout">
      <div className="newspaper-container">
        <h1 className="newspaper-title">THE VIRTUAL TIMES</h1>
        <p className="sub-title">Your Trusted Daily Source</p>
        <div className="separator"></div>

        <div className="main-headline">
          <h2>Pakistanâ€™s FIA to Collaborate with CBI on Fighting Terror</h2>
          <p>Intense cross-border security discussions are underway to curb international crime and ensure stability...</p>
        </div>

        {/* Render Ad Spaces */}
        {adSpaces.map((space) => (
          <div className="ads-container" key={space.id}>
            <div
              className={`ad-space ${space.size}`}
              onClick={() => handleAdClick(space)}
            >
              ðŸ”¹ <strong>{space.label}</strong> ðŸ”¹
              {selectedAd === space.id && (
                <div>
                  <p className="ad-price">Price: â‚¹{space.price}</p>
                  <button className="book-ad-button" onClick={handleAdBooking}>
                    Book Ad
                  </button>
                </div>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default NewspaperLayout;
