import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { getNewspapers, getUserData } from "../services/api";
import api from "../services/api";
import "./BookAd.css";

const AdBooking = () => {
  const [newspapers, setNewspapers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [authChecking, setAuthChecking] = useState(true);
  const [authError, setAuthError] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const checkUser = async () => {
      try {
        setAuthChecking(true);
        const token = localStorage.getItem("token");

        if (!token) {
          setAuthError(true);
          setAuthChecking(false);
          return;
        }

        const userData = await getUserData();
        if (userData) {
          setUser(userData);
          setAuthError(false);
          fetchNewspapers();
        } else {
          localStorage.removeItem("token");
          setAuthError(true);
        }
      } catch (error) {
        console.error("Authentication error:", error);
        setAuthError(true);
      } finally {
        setAuthChecking(false);
      }
    };

    checkUser();
  }, []);

  const fetchNewspapers = async () => {
    try {
      setLoading(true);
      const data = await getNewspapers();
      setNewspapers(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error("Fetch error:", error);
      setNewspapers([]);
    } finally {
      setLoading(false);
    }
  };

  const handleNewspaperChange = (e) => {
    const selectedId = parseInt(e.target.value);
    const selectedNewspaper = newspapers.find((paper) => paper.id === selectedId);
    if (selectedNewspaper) {
      navigate(`/newspaper-layout?name=${encodeURIComponent(selectedNewspaper.name)}`);
    }
  };

  if (authChecking) {
    return (
      <div className="loading-state">
        <div className="spinner"></div>
        <p>Verifying your account...</p>
      </div>
    );
  }

  if (authError) {
    return (
      <div className="auth-error-state">
        <div className="error-icon">!</div>
        <h2>Authentication Required</h2>
        <p>Please log in to access ad booking</p>
        <button 
          className="primary-button"
          onClick={() => navigate("/login")}
        >
          Go to Login
        </button>
      </div>
    );
  }

  return (
    <main className="ad-booking-container">
      <div className="booking-header">
        <h1>Newspaper Ad Booking</h1>
        <p className="welcome-message">Welcome back, <span>{user?.name}</span></p>
      </div>

      <div className="booking-form">
        <div className="form-group">
          <label className="form-label">Select Newspaper</label>
          <select
            className="form-select"
            onChange={handleNewspaperChange}
            disabled={loading || newspapers.length === 0}
          >
            <option value="">{loading ? "Loading newspapers..." : "Choose a newspaper"}</option>
            {newspapers.map((paper) => (
              <option key={paper.id} value={paper.id}>
                {paper.name} (â‚¹{paper.price_per_word}/word)
              </option>
            ))}
          </select>
        </div>

        <div className="info-box">
          <h3>How it works</h3>
          <ol className="steps-list">
            <li>Select your preferred newspaper</li>
            <li>Choose your ad placement and size</li>
            <li>Submit your ad content</li>
            <li>Review and confirm payment</li>
          </ol>
        </div>
      </div>
    </main>
  );
};

export default AdBooking;
