import React, { useEffect, useState } from 'react';
import axios from '../services/api';
import '../styles/AdminPayments.css';

const AdminPayments = () => {
  const [payments, setPayments] = useState([]);

  useEffect(() => {
    const fetchPayments = async () => {
      try {
        const { data } = await axios.get('/api/admin/payments');
        setPayments(data);
      } catch (error) {
        console.error('Error fetching payments:', error);
      }
    };
    fetchPayments();
  }, []);

  const handleAction = async (paymentId, action) => {
    try {
      await axios.post('/api/admin/payment-action', { paymentId, action });
      alert(`Payment ${action} successfully!`);
      setPayments(payments.filter((p) => p.payment_id !== paymentId));
    } catch (error) {
      console.error(`Error ${action} payment:`, error);
      alert(`Failed to ${action} payment.`);
    }
  };

  return (
    <div className="admin-payments-container">
      <h2>Admin Payment Approval</h2>
      {payments.length === 0 ? (
        <p>No pending payments for approval.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Payment ID</th>
              <th>Newspaper</th>
              <th>Amount</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {payments.map((payment) => (
              <tr key={payment.payment_id}>
                <td>{payment.payment_id}</td>
                <td>{payment.newspaper_name}</td>
                <td>â‚¹{payment.amount}</td>
                <td>{payment.status}</td>
                <td>
                  <button onClick={() => handleAction(payment.payment_id, 'approved')}>Approve</button>
                  <button onClick={() => handleAction(payment.payment_id, 'rejected')}>Reject</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default AdminPayments;
