import React, { useState, useEffect, useCallback, useContext } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "../../styles/DeccanChroniclesBooking.css";
import { AuthContext } from "../../context/AuthContext";

const DeccanChroniclesBooking = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { user, loading } = useContext(AuthContext);

  // Retrieve required values from navigation state
  const { price: basePrice, newspaperId=1, spaceId, newspaperName } = location.state || {};

  // Form States
  const [adTitle, setAdTitle] = useState("");
  const [adContent, setAdContent] = useState("");
  const [category, setCategory] = useState("Jobs");
  const [duration, setDuration] = useState(1);
  const [totalPrice, setTotalPrice] = useState(basePrice || 0);
  const [loadingPayment, setLoadingPayment] = useState(false);

  // Function to calculate price based on content length and duration
  const calculatePrice = useCallback((content, duration) => {
    const contentLength = content.length;
    const pricePerCharacter = 0.5; // Set price per character
    const contentPrice = basePrice + contentLength * pricePerCharacter;
    return contentPrice * duration;
  }, [basePrice]);

  // Update total price when ad content or duration changes
  useEffect(() => {
    setTotalPrice(calculatePrice(adContent, duration));
  }, [adContent, duration, calculatePrice]);

  // Load Razorpay script dynamically
  const loadRazorpay = () => {
    return new Promise((resolve) => {
      const script = document.createElement('script');
      script.src = 'https://checkout.razorpay.com/v1/checkout.js';
      script.onload = () => {
        resolve(window.Razorpay);
      };
      document.body.appendChild(script);
    });
  };

  // Handle payment and booking
  const handlePaymentAndBooking = async () => {
    if (!adTitle || !adContent) {
      alert("Please fill in all fields.");
      return;
    }

    if (!user) {
      alert("Please log in to book an ad.");
      navigate("/login");
      return;
    }

    setLoadingPayment(true);

    try {
      // 1. Create order on your backend
      const orderResponse = await fetch("http://localhost:5000/api/ad-booking/create-order", {
        method: "POST",
        headers: { 
          "Content-Type": "application/json",
          Authorization: `Bearer ${user.token}`,
        },
        body: JSON.stringify({
          amount: totalPrice
        }),
      });

      if (!orderResponse.ok) {
        throw new Error('Failed to create payment order');
      }

      const order = await orderResponse.json();

      // 2. Load Razorpay script
      const Razorpay = await loadRazorpay();

      // 3. Open Razorpay checkout
      const options = {
        key: 'rzp_test_YOUR_TEST_KEY', // Replace with your test key
        amount: order.amount,
        currency: order.currency,
        name: newspaperName || 'Newspaper Ad Booking',
        description: `Ad: ${adTitle.substring(0, 30)}...`,
        order_id: order.id,
        handler: async (response) => {
          // Verify payment on your backend
          const verificationResponse = await fetch("http://localhost:5000/api/ad-booking/verify-payment", {
            method: "POST",
            headers: { 
              "Content-Type": "application/json",
              Authorization: `Bearer ${user.token}`,
            },
            body: JSON.stringify({
              order_id: response.razorpay_order_id,
              payment_id: response.razorpay_payment_id,
              signature: response.razorpay_signature
            }),
          });

          if (!verificationResponse.ok) {
            throw new Error('Payment verification failed');
          }

          // Complete the booking
          await completeBooking(response.razorpay_payment_id);
        },
        prefill: {
          name: user.name || 'Customer',
          email: user.email,
          contact: '9999999999' // Default test number
        },
        theme: {
          color: '#3399cc'
        }
      };

      const rzp = new Razorpay(options);
      rzp.open();
      
    } catch (error) {
      console.error('Payment error:', error);
      alert(`Payment failed: ${error.message}`);
    } finally {
      setLoadingPayment(false);
    }
  };

  // Complete booking after successful payment
  const completeBooking = async (paymentId) => {
    try {
      const response = await fetch("http://localhost:5000/api/ad-booking/book", {
        method: "POST",
        headers: { 
          "Content-Type": "application/json",
          Authorization: `Bearer ${user.token}`,
        },
        body: JSON.stringify({
          userId: user.id,
          newspaperId,
          spaceId,
          title: adTitle,
          content: adContent,
          category,
          duration,
          price: totalPrice,
          newspaperName,
          email: user.email,
          paymentId
        }),
      });

      if (response.ok) {
        alert(`ðŸŽ‰ Ad booked successfully for ${duration} day(s) at â‚¹${totalPrice}!`);
        navigate("/"); // Redirect to home or confirmation page
      } else {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Booking failed');
      }
    } catch (error) {
      console.error('Booking error:', error);
      alert(`Booking failed: ${error.message}`);
    }
  };

  if (loading) return <p>Loading user data...</p>;

  return (
    <div className="deccan-booking">
      <div className="booking-card">
        <h1 className="newspaper-title">{newspaperName || "Deccan Chronicles"}</h1>
        <p className="subtitle">Your Trusted Daily Newspaper</p>

        <h2 className="section-title">Classified Ads Booking</h2>

        <div className="form-group">
          <label>Ad Title:</label>
          <input
            type="text"
            value={adTitle}
            onChange={(e) => setAdTitle(e.target.value)}
            placeholder="Enter your ad title"
            required
          />
        </div>

        <div className="form-group">
          <label>Ad Content:</label>
          <textarea
            value={adContent}
            onChange={(e) => setAdContent(e.target.value)}
            placeholder="Write your ad content here..."
            required
          />
        </div>

        <div className="form-group">
          <label>Category:</label>
          <select value={category} onChange={(e) => setCategory(e.target.value)}>
            <option value="Jobs">Jobs</option>
            <option value="Real Estate">Real Estate</option>
            <option value="Services">Services</option>
            <option value="Announcements">Announcements</option>
          </select>
        </div>

        <div className="form-group">
          <label>Duration (in days):</label>
          <input
            type="number"
            value={duration}
            onChange={(e) => setDuration(Math.min(10, Math.max(1, e.target.value)))}
            min="1"
            max="10"
            placeholder="Enter number of days (Max 10)"
            required
          />
        </div>

        <p><strong>Total Price:</strong> â‚¹{totalPrice}</p>

        <button 
          className="book-ad-button" 
          onClick={handlePaymentAndBooking}
          disabled={loadingPayment}
        >
          {loadingPayment ? 'Processing...' : 'Proceed to Payment'}
        </button>
      </div>
    </div>
  );
};

export default DeccanChroniclesBooking;
