import React, { useState } from "react";
import "../../styles/NewspaperLayout.css"; // Ensure CSS path is correct

const NewspaperLayout = () => {
  const [selectedAd, setSelectedAd] = useState(null);
  const [selectedPrice, setSelectedPrice] = useState(null);

  // Prices for different ad sizes
  const adPrices = {
    large: 5000,
    medium: 3000,
    small: 1500,
  };

  const handleAdClick = (size, index) => {
    setSelectedAd(index);
    setSelectedPrice(adPrices[size]);
  };

  return (
    <div className="newspaper-container">
      <div className="newspaper-content">
        {/* Newspaper Header with Separation */}
        <div className="newspaper-header">
          <h1 className="newspaper-title">THE VIRTUAL TIMES</h1>
          <p className="sub-title">Your Trusted Daily Source</p>
        </div>

        {/* Headline Section */}
        <div className="main-headline">
          <h2>Pakistan’s FIA to Collaborate with CBI on Fighting Terror</h2>
          <p>
            Intense cross-border security discussions are underway to curb
            international crime and ensure stability...
          </p>
        </div>

        {/* Advertisement Section */}
        <div className="ads-container">
          <div
            className="ad-space large"
            onClick={() => handleAdClick("large", 0)}
          >
            🔹 <strong>Exclusive Front Page Ad Space</strong> 🔹
            {selectedAd === 0 && <p className="ad-price">Price: ₹{selectedPrice}</p>}
          </div>

          <div className="ad-row">
            <div
              className="ad-space medium"
              onClick={() => handleAdClick("medium", 1)}
            >
              🔹 <strong>Premium Half-Page Ad</strong> 🔹
              {selectedAd === 1 && <p className="ad-price">Price: ₹{selectedPrice}</p>}
            </div>

            <div
              className="ad-space small"
              onClick={() => handleAdClick("small", 2)}
            >
              🔹 <strong>Quarter-Page Ad</strong> 🔹
              {selectedAd === 2 && <p className="ad-price">Price: ₹{selectedPrice}</p>}
            </div>
          </div>

          <div className="ad-row">
            <div
              className="ad-space small"
              onClick={() => handleAdClick("small", 3)}
            >
              🔹 <strong>Compact Ad Spot</strong> 🔹
              {selectedAd === 3 && <p className="ad-price">Price: ₹{selectedPrice}</p>}
            </div>

            <div
              className="ad-space medium"
              onClick={() => handleAdClick("medium", 4)}
            >
              🔹 <strong>Half-Page Visibility</strong> 🔹
              {selectedAd === 4 && <p className="ad-price">Price: ₹{selectedPrice}</p>}
            </div>
          </div>

          <div
            className="ad-space large"
            onClick={() => handleAdClick("large", 5)}
          >
            🔹 <strong>Prime Full-Width Ad Spot</strong> 🔹
            {selectedAd === 5 && <p className="ad-price">Price: ₹{selectedPrice}</p>}
          </div>
        </div>
      </div>
    </div>
  );
};

export default NewspaperLayout;
