import React, { useState } from 'react';

const PaymentPage = () => {
  const [cardNumber, setCardNumber] = useState('');
  const [expiryDate, setExpiryDate] = useState('');
  const [cvv, setCvv] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  const handlePayment = async (event) => {
    event.preventDefault();

    // Validate inputs
    if (!isValidCardNumber(cardNumber)) {
      setError('Invalid card number. Please enter a valid number.');
      return;
    }
    if (!isValidExpiryDate(expiryDate)) {
      setError('Invalid expiry date. Please enter a valid date (MM/YYYY).');
      return;
    }
    if (!isValidCVV(cvv)) {
      setError('Invalid CVV. Please enter a valid 3 or 4 digit CVV.');
      return;
    }

    setLoading(true);
    setError(null);
    setSuccess(false);

    const paymentData = { cardNumber, expiryDate, cvv };

    try {
      const response = await fakePaymentApi(paymentData);

      if (response.success) {
        setSuccess(true);
      } else {
        setError(response.message || 'Payment failed.');
      }
    } catch (err) {
      console.error("Error processing payment:", err);
      setError('An error occurred during payment processing. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  // Validation Functions
  const isValidCardNumber = (number) => {
    return /^\d{16}$/.test(number); // 16-digit number
  };

  const isValidExpiryDate = (date) => {
    return /^(0[1-9]|1[0-2])\/\d{4}$/.test(date); // MM/YYYY format
  };

  const isValidCVV = (cvv) => {
    return /^\d{3,4}$/.test(cvv); // 3 or 4 digit CVV
  };

  // Simulated Payment API Call
  const fakePaymentApi = async (data) => {
    return new Promise((resolve) => {
      setTimeout(() => {
        resolve({ success: true }); // Simulates a successful payment response
      }, 1000);
    });
  };

  return (
    <div style={{ padding: '20px', maxWidth: '400px', margin: 'auto' }}>
      <h1>Payment Page</h1>
      <form onSubmit={handlePayment}>
        <div style={{ marginBottom: '15px' }}>
          <label>
            Card Number:
            <input
              type="text"
              value={cardNumber}
              onChange={(e) => setCardNumber(e.target.value)}
              placeholder="1234 5678 9012 3456"
              maxLength="16"
              required
            />
          </label>
        </div>
        <div style={{ marginBottom: '15px' }}>
          <label>
            Expiry Date (MM/YYYY):
            <input
              type="text"
              value={expiryDate}
              onChange={(e) => setExpiryDate(e.target.value)}
              placeholder="MM/YYYY"
              maxLength="7"
              required
            />
          </label>
        </div>
        <div style={{ marginBottom: '15px' }}>
          <label>
            CVV:
            <input
              type="text"
              value={cvv}
              onChange={(e) => setCvv(e.target.value)}
              placeholder="123"
              maxLength="4"
              required
            />
          </label>
        </div>
        {loading ? (
          <p>Loading...</p>
        ) : (
          <button type="submit">Pay Now</button>
        )}
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {success && <p style={{ color: 'green' }}>Payment was successful!</p>}
      </form>
    </div>
  );
};

export default PaymentPage;
