import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../../styles/NewspaperLayout.css";

const NewspaperLayout = () => {
  const [selectedAd, setSelectedAd] = useState(null);
  const [selectedPrice, setSelectedPrice] = useState(null);
  const [selectedSize, setSelectedSize] = useState("");
  const [bookedSpaces, setBookedSpaces] = useState([]);
  const navigate = useNavigate();

  // Prices for different ad sizes
  const adPrices = {
    large: 5000,
    medium: 3000,
    small: 1500,
  };

  // Fetch booked ad spaces (Assuming API response format: [{ id: 1, status: "booked" }, ...])
  useEffect(() => {
    const fetchBookedSpaces = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/ad-spaces/1");
        const data = await response.json();

        // Store only IDs of booked spaces
        const booked = data
          .filter((space) => space.status === "booked")
          .map((s) => s.id);

        setBookedSpaces(booked);
      } catch (error) {
        console.error("Error fetching ad spaces:", error);
      }
    };
    fetchBookedSpaces();
  }, []);

  // Handle ad click (prevents selection if ad is booked)
  const handleAdClick = (size, index) => {
    const spaceId = index + 1; // Each ad gets a unique spaceId
    if (bookedSpaces.includes(spaceId)) return; // Prevent selecting booked ads

    setSelectedAd(index);
    setSelectedPrice(adPrices[size]);
    setSelectedSize(size);
  };

  // Navigate to booking page with ad details
  const handleAdBooking = () => {
    navigate("/deccan-chronicles-booking", {
      state: {
        size: selectedSize,
        price: selectedPrice,
        newspaperId: 1,
        newspaperName: "The Virtual Times",
        spaceId: selectedAd + 1,
      },
    });
  };

  // Ad Space Component
  const AdSpace = ({ size, index, label }) => {
    const spaceId = index + 1;
    const isBooked = bookedSpaces.includes(spaceId);

    return (
      <div
        className={`ad-space ${size}`}
        onClick={() => !isBooked && handleAdClick(size, index)} // Ensure click only works if not booked
        style={{
          cursor: isBooked ? "not-allowed" : "pointer",
          opacity: isBooked ? 0.5 : 1,
        }}
      >
        ðŸ”¹ <strong>{label}</strong> ðŸ”¹
        {isBooked && <p className="booked-label">Booked</p>}
        {selectedAd === index && !isBooked && (
          <div>
            <p className="ad-price">Price: â‚¹{selectedPrice}</p>
            <button className="book-ad-button" onClick={handleAdBooking}>
              Book Ad
            </button>
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="newspaper-layout">
      <div className="newspaper-container">
        <h1 className="newspaper-title">THE VIRTUAL TIMES</h1>
        <p className="sub-title">Your Trusted Daily Source</p>
        <div className="separator"></div>

        <div className="main-headline">
          <h2>Pakistanâ€™s FIA to Collaborate with CBI on Fighting Terror</h2>
          <p>
            Intense cross-border security discussions are underway to curb
            international crime and ensure stability...
          </p>
        </div>

        {/* Ad Sections */}
        <div className="ads-container">
          <AdSpace size="large" index={0} label="Exclusive Front Page Ad Space" />
        </div>

        <div className="ads-container">
          <AdSpace size="medium" index={1} label="Premium Half-Page Ad" />
          <AdSpace size="small" index={2} label="Quarter-Page Ad" />
        </div>

        <div className="ads-container">
          <AdSpace size="large" index={3} label="Prime Full-Width Ad Spot" />
        </div>
      </div>
    </div>
  );
};

export default NewspaperLayout;
