import React, { useState } from "react";
import "../../styles/NewspaperLayout.css"; // Ensure CSS path is correct

const NewspaperLayout = () => {
  const [selectedAd, setSelectedAd] = useState(null);
  const [selectedPrice, setSelectedPrice] = useState(null);

  // Prices for different ad sizes
  const adPrices = {
    large: 5000,
    medium: 3000,
    small: 1500,
  };

  const handleAdClick = (size, index) => {
    setSelectedAd(index);
    setSelectedPrice(adPrices[size]);
  };

  return (
    <div className="newspaper-container">
      <div className="newspaper-content">
        {/* Newspaper Header */}
        <h1 className="newspaper-title">THE VIRTUAL TIMES</h1>
        <p className="sub-title">Your Trusted Daily Source</p>
        <div className="separator"></div> {/* Separation Line */}

        {/* Main Headline */}
        <div className="main-headline">
          <h2>Pakistanâ€™s FIA to Collaborate with CBI on Fighting Terror</h2>
          <p>Intense cross-border security discussions are underway to curb international crime and ensure stability...</p>
        </div>

        {/* First Ad Section */}
        <div className="ads-container">
          <div className="ad-space large" onClick={() => handleAdClick("large", 0)}>
            ðŸ”¹ **Exclusive Front Page Ad Space** ðŸ”¹  
            {selectedAd === 0 && <p className="ad-price">Price: â‚¹{selectedPrice}</p>}
          </div>
        </div>

        {/* Multiple Fake News Articles */}
        {[
          {
            title: "Tech Giants Battle Over AI Supremacy",
            content: "Leading technology companies are competing fiercely to develop the most advanced AI models, pushing innovation to new heights..."
          },
          {
            title: "Scientists Discover Possible Signs of Life on Distant Planet",
            content: "Researchers at NASA have detected potential bio-signatures on a newly discovered exoplanet, sparking excitement among the scientific community..."
          },
          {
            title: "Stock Markets Hit Record Highs Amid Economic Recovery",
            content: "The global stock markets have reached all-time highs, driven by strong corporate earnings and renewed investor confidence..."
          },
          {
            title: "Climate Change Conference Concludes with New Global Agreements",
            content: "World leaders have signed a historic agreement to reduce carbon emissions and combat climate change, setting ambitious goals for the future..."
          },
          {
            title: "New Smartphone Release Breaks Sales Records",
            content: "The latest flagship smartphone has taken the market by storm, with millions of units sold within the first 24 hours of launch..."
          },
          {
            title: "SpaceX Successfully Launches Next-Gen Satellite",
            content: "Elon Musk's SpaceX has successfully launched a new generation of satellites, promising faster global internet coverage..."
          },
          {
            title: "Breakthrough in Cancer Treatment Announced",
            content: "Scientists have developed a new cancer treatment with promising results, increasing survival rates by 80%..."
          },
          {
            title: "Revolutionary Electric Car Achieves 1,000 Km Range on Single Charge",
            content: "A new electric vehicle prototype has shattered records with an ultra-efficient battery capable of long-distance travel..."
          }
        ].map((article, index) => (
          <div key={index} className="news-article">
            <h2>{article.title}</h2>
            <p>{article.content}</p>
          </div>
        ))}

        {/* Ad Sections in Between News */}
        <div className="ads-container">
          <div className="ad-space medium" onClick={() => handleAdClick("medium", 1)}>
            ðŸ”¹ **Premium Half-Page Ad** ðŸ”¹  
            {selectedAd === 1 && <p className="ad-price">Price: â‚¹{selectedPrice}</p>}
          </div>

          <div className="ad-space small" onClick={() => handleAdClick("small", 2)}>
            ðŸ”¹ **Quarter-Page Ad** ðŸ”¹  
            {selectedAd === 2 && <p className="ad-price">Price: â‚¹{selectedPrice}</p>}
          </div>
        </div>

        <div className="ads-container">
          <div className="ad-space small" onClick={() => handleAdClick("small", 3)}>
            ðŸ”¹ **Compact Ad Spot** ðŸ”¹  
            {selectedAd === 3 && <p className="ad-price">Price: â‚¹{selectedPrice}</p>}
          </div>

          <div className="ad-space medium" onClick={() => handleAdClick("medium", 4)}>
            ðŸ”¹ **Half-Page Visibility** ðŸ”¹  
            {selectedAd === 4 && <p className="ad-price">Price: â‚¹{selectedPrice}</p>}
          </div>
        </div>

        <div className="ads-container">
          <div className="ad-space large" onClick={() => handleAdClick("large", 5)}>
            ðŸ”¹ **Prime Full-Width Ad Spot** ðŸ”¹  
            {selectedAd === 5 && <p className="ad-price">Price: â‚¹{selectedPrice}</p>}
          </div>
        </div>

        <div className="ads-container">
          <div className="ad-space small" onClick={() => handleAdClick("small", 6)}>
            ðŸ”¹ **Limited-Time Ad Offer** ðŸ”¹  
            {selectedAd === 6 && <p className="ad-price">Price: â‚¹{selectedPrice}</p>}
          </div>

          <div className="ad-space medium" onClick={() => handleAdClick("medium", 7)}>
            ðŸ”¹ **Top Visibility Ad** ðŸ”¹  
            {selectedAd === 7 && <p className="ad-price">Price: â‚¹{selectedPrice}</p>}
          </div>
        </div>

        <div className="ads-container">
          <div className="ad-space large" onClick={() => handleAdClick("large", 8)}>
            ðŸ”¹ **Ultimate Full-Page Ad Placement** ðŸ”¹  
            {selectedAd === 8 && <p className="ad-price">Price: â‚¹{selectedPrice}</p>}
          </div>
        </div>

      </div>
    </div>
  );
};

export default NewspaperLayout;
