import { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import AuthContext from "../context/AuthContext";

const Login = () => {
  const { login } = useContext(AuthContext); // Use AuthContext for authentication
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(""); // Clear previous errors

    try {
      await login(email, password); // Use AuthContext login function
      navigate("/ad-booking"); // Redirect to Ad Booking page after login
    } catch (err) {
      setError("❌ Login failed: " + (err.message || "Invalid credentials"));
    }
  };

  return (
    <div>
      <h2>Login</h2>
      {error && <p style={{ color: "red", fontWeight: "bold" }}>{error}</p>}
      
      <form onSubmit={handleLogin}>
        <label>Email:</label>
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />

        <label>Password:</label>
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />

        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
