import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../../styles/NewspaperLayout.css";

const NewspaperLayout = () => {
  const [selectedAd, setSelectedAd] = useState(null);
  const [selectedPrice, setSelectedPrice] = useState(null);
  const [selectedSize, setSelectedSize] = useState("");
  const [bookedAds, setBookedAds] = useState([false, false, false, false]); // Track booked ads
  const navigate = useNavigate();

  const adPrices = {
    large: 5000,
    medium: 3000,
    small: 1500,
  };

  const handleAdClick = (size, index) => {
    if (bookedAds[index]) {
      return; // Prevent selection if the ad is booked
    }
    setSelectedAd(index);
    setSelectedPrice(adPrices[size]);
    setSelectedSize(size);
  };

  const handleAdBooking = () => {
    setBookedAds((prev) => {
      const newBookedAds = [...prev];
      newBookedAds[selectedAd] = true; // Mark the selected ad as booked
      return newBookedAds;
    });

    navigate("/deccan-chronicles-booking", {
      state: {
        size: selectedSize,
        price: selectedPrice,
        newspaperId: 1,
        newspaperName: "The Virtual Times",
        spaceId: selectedAd + 1,
      },
    });
  };

  return (
    <div className="newspaper-layout">
      <div className="newspaper-container">
        <h1 className="newspaper-title">THE VIRTUAL TIMES</h1>
        <p className="sub-title">Your Trusted Daily Source</p>
        <div className="separator"></div>

        <div className="main-headline">
          <h2>Pakistanâ€™s FIA to Collaborate with CBI on Fighting Terror</h2>
          <p>Intense cross-border security discussions are underway to curb international crime and ensure stability...</p>
        </div>

        {/* Ad Spaces */}
        <div className="ads-container">
          <div className={`ad-space large ${bookedAds[0] ? 'booked' : ''}`} onClick={() => handleAdClick("large", 0)}>
            ðŸ”¹ <strong>Exclusive Front Page Ad Space</strong> ðŸ”¹  
            {selectedAd === 0 && (
              <div>
                <p className="ad-price">Price: â‚¹{selectedPrice}</p>
                <button className="book-ad-button" onClick={handleAdBooking}>Book Ad</button>
              </div>
            )}
          </div>
        </div>

        <div className="ads-container">
          <div className={`ad-space medium ${bookedAds[1] ? 'booked' : ''}`} onClick={() => handleAdClick("medium", 1)}>
            ðŸ”¹ <strong>Premium Half-Page Ad</strong> ðŸ”¹  
            {selectedAd === 1 && (
              <div>
                <p className="ad-price">Price: â‚¹{selectedPrice}</p>
                <button className="book-ad-button" onClick={handleAdBooking}>Book Ad</button>
              </div>
            )}
          </div>

          <div className={`ad-space small ${bookedAds[2] ? 'booked' : ''}`} onClick={() => handleAdClick("small", 2)}>
            ðŸ”¹ <strong>Quarter-Page Ad</strong> ðŸ”¹  
            {selectedAd === 2 && (
              <div>
                <p className="ad-price">Price: â‚¹{selectedPrice}</p>
                <button className="book-ad-button" onClick={handleAdBooking}>Book Ad</button>
              </div>
            )}
          </div>
        </div>

        <div className="ads-container">
          <div className={`ad-space large ${bookedAds[3] ? 'booked' : ''}`} onClick={() => handleAdClick("large", 3)}>
            ðŸ”¹ <strong>Prime Full-Width Ad Spot</strong> ðŸ”¹  
            {selectedAd === 3 && (
              <div>
                <p className="ad-price">Price: â‚¹{selectedPrice}</p>
                <button className="book-ad-button" onClick={handleAdBooking}>Book Ad</button>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default NewspaperLayout;
