// PaymentPage.js

import React, { useState } from 'react';
import axios from 'axios';
import '../styles/BookAd.css';

const PaymentPage = ({ adSpaceId, adContent, userId, onPaymentSubmit }) => {
    const [paymentProof, setPaymentProof] = useState(null);

    const handleFileChange = (e) => {
        setPaymentProof(e.target.files[0]);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!paymentProof) {
            alert('Please upload payment proof');
            return;
        }

        const formData = new FormData();
        formData.append('userId', userId);
        formData.append('adSpaceId', adSpaceId);
        formData.append('adContent', adContent);
        formData.append('paymentProof', paymentProof);

        try {
            await axios.post('/api/ad-booking/book-ad', formData, {
                headers: { 'Content-Type': 'multipart/form-data' }
            });

            alert('Payment proof submitted for approval');
            if (onPaymentSubmit) onPaymentSubmit();

        } catch (error) {
            console.error('Error submitting payment proof:', error);
            alert('Failed to submit payment proof');
        }
    };

    return (
        <div className="payment-container">
            <h2>Upload Payment Proof</h2>
            <form onSubmit={handleSubmit}>
                <input type="file" onChange={handleFileChange} accept="image/*,application/pdf" />
                <button type="submit">Submit Payment Proof</button>
            </form>
        </div>
    );
};

export default PaymentPage;
