import axios from "axios";

// ‚úÖ Ensure API_BASE_URL is defined only once
const API_BASE_URL = "http://localhost:5000/api";

// ‚úÖ Create a single Axios instance
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    "Content-Type": "application/json",
  },
});

// ‚úÖ Helper: Retrieve token from localStorage
export const getToken = () => {
  const token = localStorage.getItem("token");
  console.log("üîç Retrieved Token:", token);
  return token;
};

// ‚úÖ Automatically add Authorization header if token exists
api.interceptors.request.use(
  (config) => {
    const token = getToken();
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// Add response interceptor
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      console.warn("Authentication token is invalid or expired");
      localStorage.removeItem("token");
    }
    return Promise.reject(error);
  }
);

// ‚úÖ Export all API functions that were missing
export const getUserData = async () => {
  try {
    const token = getToken();
    if (!token) {
      console.warn("No token found when getting user data");
      return null;
    }
    
    const response = await api.get("/auth/user");
    return response.data?.user || response.data;
  } catch (error) {
    console.error("‚ùå Error fetching user data:", error.response?.data || error.message);
    return null;
  }
};

export const getNewspapers = async () => {
  try {
    const response = await api.get("/newspapers");
    return response.data;
  } catch (error) {
    console.error("‚ùå Fetch Newspapers Error:", error);
    return [];
  }
};

// ‚úÖ Keep all existing functions exactly as they were
export const loginUser = async (email, password) => {
  try {
    const response = await api.post("/auth/login", { email, password });

    if (response.data.token) {
      localStorage.setItem("token", response.data.token);
      console.log("‚úÖ Token saved:", response.data.token);
    }

    return response.data;
  } catch (error) {
    console.error("‚ùå Login Error:", error.response?.data || error.message);
    throw error;
  }
};

export const bookAd = async (adData) => {
  try {
    const response = await api.post("/ad-booking", adData);
    console.log("‚úÖ Ad booked successfully:", response.data);
    return response.data;
  } catch (error) {
    console.error("‚ùå Error booking ad:", error.response?.data || error.message);
    throw error;
  }
};

export const signupUser = async (userData) => {
  try {
    const response = await api.post("/auth/signup", userData);
    return response.data;
  } catch (error) {
    console.error("‚ùå Signup Error:", error.response?.data || error.message);
    throw error;
  }
};

export const logoutUser = () => {
  localStorage.removeItem("token");
  console.log("üö™ User logged out successfully.");
};

export const getAuthenticatedUser = () => {
  const token = getToken();
  if (!token) return null;
  try {
    const payload = JSON.parse(atob(token.split(".")[1]));
    console.log("üîç Decoded Token Payload:", payload);
    return payload;
  } catch (error) {
    console.error("‚ùå Token decoding error:", error);
    return null;
  }
};

// ‚úÖ Export the Axios instance
export default api;
